@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("accuweather.com") {

div, #content_column_300right {display: none !important}

div[id*=Sale], div[id*=Center], div.bottom_content_links {display: none !important;width:0px !important;height:0px !important;overflow:hidden !important;margin:0px !important;}

#content_container_2col660, #page_container, #content_container_3col, #content_column_435, div.Div1,#searchnav_container,#searchnav_container div, #content_column_435 div[id], #content_column_435 div[id] div, #forecast_header + .content_box_435, #forecast_header + div:not([class]), #forecast_header + div:not([class]) div, #forecast_header + div:not([class]) + .content_box_435, #forecast_header + div:not([class]) + .content_box_435 div, #forecast_header + .content_box_435 div, #forecast_header + .content_box_435 + .content_box_435, #forecast_header + .content_box_435 + .content_box_435 div, a[style] + .content_box_435, a[style] + .content_box_435 div, a[style] div, #content_box_bluenavbutton_hbhcomplete, .forecast_titlebar, .forecast_titlebar div, #content_column_660, #content_column_660 div[id], #content_column_660 div[id] div, div[class*=content_box_660], div[class*=content_box_660] div {
display: block !important;
}



#searchnav_container {
height: 28px !important;
overflow: hidden !important;
}

#content_column_435, #content_container_3col, #content_container_2col660, #content_column_660 {
width:100% !important;
padding: 15px;
text-align:center;
}

.content_box_435, .content_box_435body {
padding:0px !important;
margin:0px !important;
}

.content_box_435, div[id*=column] > a[style], div[id*=column] > a {
margin-left:10px !important;
margin-bottom:10px !important;
border: 1px solid #666;
}

.content_box_435 .content_box_435 {
margin:0px !important;
border:none;
}

#content_column_435 > div:not(.content_box_435), .forecastButtons {
max-width: 92%;
}

#forecast_header, #fcst_date, .fcst_date, .forecast_titlebar,#content_column_660 {
margin-left:auto !important;
margin-right:auto !important;
text-align:center !important;
}

#content_box_bluenavbutton_hbhcomplete {
margin-left:5px;
}

div[id*=content_box], div[class*=content_box], #content_box_bluenavbutton_hbhcomplete {
float:left;
}

#forecast_header + .content_box_435, a[style] + .content_box_435, .content_box_435bluenavbar + .class_box_435 {
float:none;
}


}
