css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('https://www.tumblr.com/login'),
url('http://www.tumblr.com/dashboard'),
url('http://www.tumblr.com/new/blog'),
url('http://www.tumblr.com/inbox'),
url('http://www.tumblr.com/help'),
url('https://www.tumblr.com/preferences'),
url('http://www.tumblr.com/lookup'),
url('http://www.tumblr.com/explore'),
url('http://www.tumblr.com/following'),
url('http://www.tumblr.com/likes'),
url-prefix('http://www.tumblr.com/blog/'),
url-prefix('http://www.tumblr.com/dashboard/'),
url-prefix('http://www.tumblr.com/new/'),
url-prefix('http://www.tumblr.com/tumblelog/'),
url-prefix('http://www.tumblr.com/tagged/'),
url-prefix('http://www.tumblr.com/spotlight/'),
url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {

img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],.new_post_label_icon {opacity: 0 !important;}


#new_post {
background: rgba(255,255,255, 1.0) url('http://i.imgur.com/kTncM.jpg') center center no-repeat !important;}
.new_post_label {
margin-top: 14px !important;
}


body {
  background:url("http://i.imgur.com/D221I.jpg") no-repeat fixed !important;
  background-color: rgba(44,71,98, 0.95) !important;
  background-position: 0% 0% !important;
  font: normal 11px "Tahoma";
}

#logo {
width: 300px;
height: 45px;
background-image: url('http://i.imgur.com/beNIQ.png');
background-repeat: no-repeat;
}

div.l-header .tab.iconic > a {
background-image: url('http://i.imgur.com/dy9VE.png');
background-size: 175px 36px;
background-repeat: no-repeat;
cursor: pointer;
width: 38px;
}

ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #a8bccf !important;
}

ol#posts li blockquote blockquote,
body.mceContentBody blockquote blockquote {
border-color: #839aaf !important;
}

ol#posts li blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote {
border-color: #6b7d8f !important;
}

ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #4c5e6f !important;
}

ol#posts li blockquote blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #36434f !important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(229, 187, 96, .5) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(229, 187, 96, .5) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 20px 20px;
}

h1 {
color: #666666;
text-shadow: 0 0 3px #ffffff;
}

.no_posts_found, h1.dashboard_header {
color: #666666;
}

img#content_top, img#content_bottom {
display: none;
}

#right_column .controls_section li a {
color: rgba(0, 0, 0, .69);
font-size: 11px;
font-weight: bold;
text-decoration: none;
background-image: url('http://i.imgur.com/lI7N4.png');
background-repeat: no-repeat;
display: block;
}

#right_column::after {
position: absolute;
top: 100%;
right: 0;
left: 0;
height: 35px;
background: -webkit-linear-gradient(top,rgba(118, 180, 214, 0) 0,rgba(118, 180, 214, 0) 100%);
}

#right_column .controls_section .with_subtitle small {
color: white;
font-size: 11px;
font-weight: normal;
}

div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #d9a127;
font-size: 0;
position: relative;
top: 4px;
vertical-align: top;
margin: 0 auto;
display: none;
}

ol#posts li.post .permalink {
background: #9F85F1;
}

ol#posts li.notification {
background-color: rgba(85, 74, 74, 1);
border-bottom: 1px solid white;
}

ol#posts li.notification.first_notification {
background-color: rgba(85, 74, 74, 1);
}

#posts .notification .notification_type_icon {
position: absolute;
right: 11px;
top: 10px;
background: 0 0 url('http://i.imgur.com/zdARU.png') no-repeat transparent;
}

#posts .notification {
width: 464px;
color: rgba(255, 255, 255, .45);
font-family: "Lucida Grande","Helvetica Neue",Helvetica,Arial,sans-serif;
font-size: 11px;
line-height: 19px;
background-color: #959DDB;
border-left: solid 1px white;
border-right: solid 1px white;
border-bottom: solid 1px white;
-webkit-background-clip: padding-box;
margin: 0 0 0 -1px;
padding: 7px 65px 7px 11px;
position: relative;
}

#posts .notification.single_notification {
clear: both;
border: solid 1px white;
margin-top: 20px;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
border-radius: 6px;
}

#posts .notification.alt {
background-color: #554a4a;
}

ol#posts li.notification.last_notification {
border-bottom: 1px solid white;
}

#posts .notification.first_notification {
clear: both;
border-top: solid 1px white;
margin-top: 20px;
-webkit-border-top-left-radius: 6px;
-webkit-border-top-right-radius: 6px;
-moz-border-radius-topleft: 6px;
-moz-border-radius-topright: 6px;
border-top-left-radius: 6px;
border-top-right-radius: 6px;
}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid rgba(255, 255, 255, 0);
border-right: 6px solid white;
position: absolute;
left: -5px;
top: 10px;
}

#posts .notification .nipple.border {
border-top: 7px solid transparent;
border-bottom: 7px solid transparent;
border-right: 7px solid white;
position: absolute;
left: -7px;
top: 9px;
}

#posts .notification.alt .nipple {
border-right: 6px solid white;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
background: none;
}

#right_column .controls_section li .blog small {
color: white;
font-size: 11px;
font-weight: normal;
line-height: 15px;
}

#nav .nav_item.active {
background: rgba(0,60,100,0.5) !important;
}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: rgba(0,60,100,0.5) !important;
}

ol#posts li.notification.first_notification {
background-color: rgba(85, 74, 74, 1);
}

ol#posts li.notification {
background-color: rgba(85, 74, 74, 1);
border-bottom: 1px solid #fff;
}

ol#posts li.notification.last_notification {
border-bottom: 1px solid #fff;
}

ol#posts li.post .permalink {
background: #9F85F1;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.8);
border: solid #fff;
}

ol#posts li.post .post_avatar{
border-bottom: 0px solid #0A2939;
}

form.dashboard_options_form {
background-color: rgba(0,0,0,0.7);
border-bottom: 1px solid #fff;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}

form.dashboard_options_form .option_container {
border-top: 1px solid #fff;
}

.no_posts_found, h1.dashboard_header {
color: #666666;
}}
