css:
@-moz-document domain(tumblr.com) {
body {



background: #a10000  url('http://i854.photobucket.com/albums/ab104/tygre9000/aradiabackriund1_zpsd5c4033a.png')no-repeat fixed !important;



background-attachment: fixed !important;



background-position: 0% 0% !important;



background-size: 100% 100%  !important;





}







#logo {height: 0 !important;



	width: 0 !important;



	padding-left: 240px !important;



	padding-top: 70px !important;



	background: url(http://i854.photobucket.com/albums/ab104/tygre9000/tumblr_m0ea0kgIJs1r3cxdf_zps583ac1c3.png) no-repeat !important;}







ol#posts li.notification {







  background-color: #4c0000   !important;







  border-top: 3px solid #000000 !important;







  border-left: 3px solid #000000 !important;







  border-bottom: 3px solid #000000 !important;







  border-right: 3px solid #000000 !important;







  color: #ff0000 !important;







  text-decoration: none !important;







}













#posts .notification a:not(.avatar_frame) {







color: rgba(00, 00, 00, 1) !important;







font-weight: bold;







text-decoration: none !important;







border-bottom: 0px solid #FF6326 !important;







-webkit-background-clip: padding-box;







}















#posts .notification blockquote {







  border-left: solid 3px #FF6326 !important;







}















#posts .notification .avatar_frame {







background-color: #FF8763 !important;







border: 1px solid #ff0000 !important;







position: absolute;







top: -1px;







left: -55px;







overflow: hidden;







display: inline-block;







}



















































.post.is_mine.with_avatar.new_post {







  color: #ffffff !important;







  font-family:courier, monospace !important;







  background: rgba(0,0,0,0.50) !important; 







}



















img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],



.new_post_label_icon {



    opacity: 0 !important;



}

.new_post_label{



font-size:0px !important;



}









#new_post{







  background-image: url(http://i854.photobucket.com/albums/ab104/tygre9000/zxln382_zps563c6c0a.jpg) !important; 



opacity:100.00 !important;



  background-repeat: no-repeat !important; 







}











#posts .post.new_post .new_post_label {







-webkit-font-smoothing: antialiased;







opacity: .9;







font-size: 14px;







font-weight: normal;







color: #ffffff;







text-decoration: none;







text-transform: uppercase;







text-align: center;







width: 74px;







position: absolute;







}















#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {







  background: rgba(242, 164, 0, .0) !important; 





}































.selection_nipple:not([class="selection_nipple white"]) {







border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;







}















#content_top {







  width: 0px !important;







  height: 0px !important;



















}







.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {







background: none !important;







width: 0px !important;







height: 0px !important;







margin-top: 0px !important;







border-bottom: solid 11px rgba(00,00,00, 0.50) !important;







border-right: 11px solid transparent !important;







border-left: 11px solid transparent !important;







}



















#content_bottom {







  width: 0px !important;







  height: 0px !important;







































}























#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {







  padding-top: 25px !important; 







  padding-bottom: 25px !important; 







   -moz-border-radius: 20px !important; 







}























LABEL {







  color: rgba(0,0,0, 1.0) !important; 







}



















#footer {







  display: none !important; 







}















.radar {







  display: none !important; 







}











.selected .following,







.selected .followers,







.selected .settings,







.selected .posts,







.selected .messages,







.selected .drafts,







.selected .queue,







.selected .likes  {







  color: rgba(0, 0, 0, 0.50) !important; 







  background: rgba(0, 0, 0, 0.0) !important; 







}























#new_post .arrow {







  opacity: 0.50 !important;







  margin-left: -1px !important; 







}
}
