css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") 
{

html, body, #page, #masthead-container, #watch-video-container
{
    background: #121212 !important;
    color: #ebebeb !important;
}

.branded-page-v2-primary-col
{
    background: #121212 !important;
}

.yt-tile-static:hover a, .yt-tile-visible:hover a span
{
    color: white !important;
}

#watch-headline h1, #masthead-nav a, #watch-description, .video-extras-likes-dislikes, .video-extras-likes-dislikes .dislikes, .comment-text, .metadata, #watch-sidebar .video-list-item .title, .yt-tile-visible a, .masthead-expanded-menu-item a, .video-list-item a span, .guide-item, .yt-tile-default h3 a, .feed-item-container .feed-item-main, .feed-item-main .description, .feed-header .secondary-link, .branded-page-v2-secondary-col .branded-page-module, .branded-page-v2-secondary-col .branded-page-module h2, .branded-page-v2-secondary-col .branded-page-module h2 a, .branded-page-v2-secondary-col .branded-page-module h3 a, .branded-page-v2-secondary-col .branded-page-module h3, .vm-video-title-text, .yt-uix-pager a span
{
    color: #ebebeb !important;
}

.video-list-item .stat, #masthead-expanded .list-video-facets, .feed-item-content .metadata .view-count, .vm-video-info, .vm-grid-video-list .vm-video-label, .yt-lockup-content p, #results-main-content .description b
{
    color: #bbb !important;
}

#watch-uploader-info, .watch-view-count, .comments-section h4, .guide-item .display-name, .feed-item-container a
{
    color: white !important;
}

a, #watch-description a, #watch-sidebar .video-list-item a:visited .title, .yt-tile-default h3 a:visited, .feed-item-container a:visited
{
    color: #ba3030 !important;
}

.yt-uix-button-default:active, .yt-uix-button-subscription:active, .yt-uix-button-subscription.yt-uix-button-active, .yt-uix-button-text:active, .yt-uix-button-panel .yt-uix-button-text:active, .yt-uix-button-default.yt-uix-button-active, .yt-uix-button-default.yt-uix-button-toggled, .yt-uix-button-text.yt-uix-button-active
{
    background: #ba3030 !important;
    border-color: #ba3030 !important;
}

#search-results .yt-uix-tile:hover
{
    background: #303030 !important;
    border-radius: 0 !important;
    box-shadow: none !important;
}

#vm-page-subheader, #vm-video-actions-inner
{
    background: #303030 !important;
    color: #ebebeb !important;
}

#vm-video-actions-inner, #vm-page-subheader, #vm-video-list-container
{
    border: 0 !important;
}

#yt-admin
{
    border: 0 !important;
}

#yt-admin-sidebar
{
    padding-top: 0px !important;
}

#yt-admin-sidebar li a
{
    padding: 10px !important;
    color: #ebebeb !important;
    text-shadow: none !important;
}

#yt-admin-sidebar .selected, #yt-admin-sidebar .selected:hover, #yt-admin-sidebar a:hover
{
    background: #ba3030 !important;
    color: white !important;
}

.yt-nav-dark
{
    background: #303030 !important;
    border-radius: 0 !important;
}

.yt-nav-dark .yt-nav-item
{
    color: #ebebeb !important;
}

.yt-nav-dark .yt-nav-item:hover, .yt-nav-dark .selected .yt-nav-item
{
    background: #ba3030 !important;
    color:  white !important;
    box-shadow: none !important;
}

.feed-item-container
{
    border: 0 !important;
    padding-top: 10px !important;
}

.feed-item-main
{
    border: 0 !important;
    margin-top: 0 !important;
    padding-bottom: 0px !important;
}

.feed-item-container:hover
{
    background: #ba3030 !important;
}

.feed-item-container:hover a
{
    color: white !important;
}

.branded-page-v2-primary-col
{
    border: 0px !important;
}

.guide-selected
{
    background: #ba3030 !important;
    border-color: #ba3030 !important;
    border-radius: 0px !important;
}

.branded-page-v2-header
{
    background: #303030 !important;
    margin: 0 !important;
    border-radius: 0 !important;
}

#playlist-bar-bar-container
{
    border: 0px !important;
}

#ticker
{
    background: #303030 !important;
}

.ticker-content a
{
    color: #ebebeb !important;
}

#playlist-bar-tray-container
{
    background: #121212 !important;
}

#playlist-bar-bar
{
    height: 30px !important;
    background: #303030 !important;
    border: 0px !important;
}

#playlist-bar-info .playlist-bar-group
{
    height: 30px !important;
    line-height: 30px !important;
}

.playlist-bar-group .yt-uix-button, #playlist-bar-title .yt-uix-button
{
    margin-top: -6px !important;
}

#playlist-bar-controls
{
    height: 30px !important;
}

.playlist-bar-group
{
    border: 0px !important;
}

#playlist-bar-controls .yt-uix-button
{
    height: 30px !important;
    margin-top: 1px !important;
}

.playlist-bar-count
{
    padding-top: 0px !important;
    top: 0px !important;
    color: #ebebeb !important;
}

#guide
{
    background: #303030 !important;
}

#watch-channel-discoverbox
{
    box-shadow: none !important;
}

#watch-channel-discoverbox .yt-tile-visible
{
    background: #303030 !important;
}

#watch-channel-discoverbox .yt-tile-visible:hover
{
    background: #ba3030 !important;
}

.guide-item:hover
{
    background: #ba3030 !important;
    border-color: #ba3030 !important;
    border-radius: 0 !important;
}

#watch-channel-discoverbox .yt-uix-slider-next, #watch-channel-discoverbox .yt-uix-slider-prev
{
    height: 157px !important;
}

.yt-tile-static, .yt-tile-visible
{
    background: #303030 !important;
    box-shadow: none !important;
    border-radius: 0 !important;
    border-color: #303030 !important;
}

#watch-channel-discoverbox .yt-tile-default
{
    box-shadow: none !important;
    border-radius: 0 !important;
}

.yt-uix-pager .yt-uix-pager-selected
{
    background: #ba3030 !important;
    border-color: #ba3030 !important;
}

.yt-tile-static:hover, .yt-tile-visible:hover, .yt-tile-default:hover
{
    background: #ba3030 !important;
    border-color: #ba3030 !important;
}

#masthead-expanded-container
{
    box-shadow: none !important;
    margin-bottom: 0px !important;
}

.yt-horizontal-rule
{
    display: none !important;
}

#watch-sidebar .video-list-item a:hover
{
    background: #303030 !important;
    border-color: #303030 !important;
    border-radius: 0 !important;
}

.comments-section .yt-tile-default:hover
{
    background: none !important;
    border: 0px !important;
    box-shadow: none !important;
}

#watch-description-toggle
{
    border-top: 0px !important;
}

#watch-description-expand, #watch-description-collapse
{
    background: #303030 !important;
    border-top: 0px !important;
}

#watch-description-expand:hover, #watch-description-collapse:hover
{
    background: #ba3030 !important;
}

.yt-uix-button
{
    background: #303030 !important;
    box-shadow: none !important;
    border-color: #303030 !important;
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
    border-bottom-left-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
    text-shadow: none !important;
    color: #ebebeb !important;
}

.yt-uix-button:hover
{
    background: #ba3030 !important;
    color: white !important;
    border-color: #ba3030 !important;
    box-shadow: none !important;
}

#masthead-user-wrapper
{
    border-color: black !important;
    border-radius: 0 !important;
}

#masthead-search-term
{
    background: #303030 !important;
    box-shadow: none !important;
    color: #ebebeb !important;
}

#masthead-search-terms
{
    border-color: #303030 !important;
    box-shadow: none !important;
    border-radius: 0 !important;
}

}
