css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.steampowered.com") {

HTML { background-image:url("data:image/png;base64,") !important; }

body { background-image:url("data:image/png;base64,") !important; }

.page { background:#424242 !important; border-color:#424242 !important; }

.alt1, .alt1Active, .alt2, .alt2Active { background:#323232 !important; border-color:#424242 !important; color:#FFFFFF !important; font-weight:normal !important; }

.thead { background-color:#424242 !important; border-color:#424242 !important; }

.tfoot { background:#323232 !important; border-color:#424242 !important; }

.panel { background:#323232 !important }

.panelsurround { background:#323232 !important }


.vbmenu_control { background:#181818 !important; border-color:#424242 !important; }
.tcat { background:#323232 !important; border-color:#424242 !important; color:#FFFFFF !important; font-weight:normal !important; }

.vbmenu_control a:link, .vbmenu_control_alink, .vbmenu_control a:visited, .vbmenu_control_avisited { font-weight:normal !important; color:#d0d0d0 !important; }

#content { font-family:Calibri,Verdana,Arial,Helvetica,sans-serif !important; font-size-adjust:.65 !important; }

li { font-size:1em !important; line-height:1.5em !important; }

ol>li { list-style-type:decimal !important; }

}
