css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

.fbSidebarGripper div,.fbChatSidebar #pagelet_ticker {
display:none!important;}

.fbChatSidebar .scrollableOrderedList {
    height: 284px!important;
}
.sidebarMode .fbx #pageHead, .sidebarMode #blueBar #pageHead,.sidebarMode .fbx #globalContainer {
    padding-right: 0px!important;
}
#fbDockChatBuddylistNub{display:block!important;
right:0px!important;
position:relative!important;}
.sidebarMode .fbChatSidebar {
height:284px!important;
position:fixed!important;
margin-right:14px!important;
top:auto!important;
bottom:23px!important;
width:200px!important;}
.fbDockChatBuddyListNub .fbNubFlyoutInner .fbDockChat{right:0px!important;}


.fbChatSidebar .fbChatTypeaheadView {
    position: absolute;
    top: 0!important;
}
._4oes {
    border-right: 1px solid #999!important;
    bottom: 284px!important;
    border-left: 1px solid #999!important;
    position: absolute;
width:202px!important;
left:-1px;}
#uy92sbu46{position:relative!important;
bottom:24px!important;
right:74px!important;
width:200px!important;}
#uy92sbu45{position:relative!important;
margin-left:129px!important;
}
.toggle {
    background-image: url(http://i656.photobucket.com/albums/uu284/UserStyles/chatbarv3_zps591221b6.png) !important;
    background-position: 0px 0px!important;
    background-repeat: no-repeat;
    position: fixed;
    bottom: 329px!important;
    right:14px!important;
width:202px!important;
border:none!important;
background-color:transparent!important;}
#uhqz37p46.toggle.button{width:129px!important;
}
#fbDockChatTabs{right:0px!important;}

.sidebarMode .fbDockWrapper {
    right: 0px!important;
}

.fbChatSidebar {
background-color: #fff!important;
border-left: 1px solid rgba(0, 0, 0, 0.4)!important;
border-right: 1px solid rgba(0, 0, 0, 0.4)!important;
border-bottom: 1px solid rgba(0, 0, 0, 0.4)!important;
border-top:none!important;}

.fbChatOrderedList .item a:hover, .fbChatOrderedList .item a.selected {
    background-color: rgba(59,89,152,0.1)!important;

}
.fbChatSidebar .tickerStoryImage, .fbChatSidebar .fbChatOrderedList .item .pic, .fbChatSidebar .fbChatTypeaheadView li img {
width:16px!important;
height:18px!important;
}
.fbChatSidebar .fbChatOrderedList, .fbChatSidebar .fbChatTypeaheadView {
padding-top: 0!important;
}
.fbChatOrderedList .item .name {
line-height: 16px!important;
}
.fbChatOrderedList .item .icons {
height: 16px!important;
line-height: 13px!important;}

.uiTooltipX {
    right: 8px!important;
}

.fbChatSidebar .fbChatSidebarFooter {
    border-top: 0px solid #C9D0DA!important;
    right: -1px!important;
    width: 201px!important;
}
}
