css:
@-moz-document domain("friendcodes.com") {
#cometchat_base {
background: transparent url(http://i306.photobucket.com/albums/nn247/hello2u1/chatbar-3.png) repeat-x top left !important; !important;
border-top:none !important;
}
#cometchat_chatbox_right,#cometchat_chatbox_left {
border-left:1px solid black !important;
border-right:none !important;
border-top:1px solid black !important;
border-top-left-radius: 15px !important;
border-top-right-radius: 15px !important;
border-top:0px solid transparent !important;
color:white !important;
background: /*[[color]]*/ !important;
position:relative;
top:4px;
text-shadow: 1px 1px 0 black !important;
padding-left:2px !important;
padding-right:0px !important;
margin-right:2px;
margin-left:2px;
}
.cometchat_tab {
background-color: /*[[color]]*/ !important;
background-image:url(http://i306.photobucket.com/albums/nn247/hello2u1/chatbar-3.png);
border-bottom: 1px black !important;
border-bottom-width: 1px !important;
}
.cometchat_trayicon {
background: /*[[color]]*/ url(http://i306.photobucket.com/albums/nn247/hello2u1/chatbar-3.png) repeat-x top left !important;
}
.cometchat_userstabclick {
background: /*[[color]]*/ url(http://i306.photobucket.com/albums/nn247/hello2u1/chatbar-3.png) repeat-x top left !important;
border-bottom:1px solid black !important;
}
.cometchat_tabclick {
border-bottom:1px solid black !important;
}

#cometchat_hide {
padding-top:7px !important;
top: 3px !important;
position:relative !important;
background-color: /*[[color]]*/ !important;
}
.cometchat_userstabtitle,.cometchat_traytitle {
background: /*[[color]]*/ url(http://i306.photobucket.com/albums/nn247/hello2u1/chatbar-3.png) repeat-x top left !important;
}
#cometchat_trayicon_themechanger,#cometchat_trayicon_share,#cometchat_trayicon_announcements {
display:none;
}
}
