css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opt.dclink.com.ua")
{ 
body {
    color: #2A2A2A;
    background-color: #CFF8A6 !important;
    font-family: Arial,Helvetica,sans-serif;
    font-size: 11px !important;
    height: 100%;
    margin: 0;
    padding: 0;
    position: relative;
    width: 100%;
}

div.main {
	/*position: relative;*/
	width: 100% !important;
	margin: 0 auto;
	display: block;
	padding: 0;
    height: 100%;
} 
div.left {
    position: absolute !important; /* Абсолютное позиционирование */
    top: 200px; /* Положение от нижнего края */
    left: 150px; /* Положение от правого края */
    float: left !important;
    margin-right: 15px !important; 
    padding-left: 9px;
    width: 320px !important;
    display: inline !important; 
    background-color: #CFF8A6 !important;
    }
    
div.center {
    float: right !important;
    width: 500px;
}

input, textarea {
    font-family: Arial,Helvetica,sans-serif;
    font-size: 10px !important;
    outline: medium none;
}

div.price {
    color: #ED2836;
    float: right;
    font-size: 12px !important;
}

h1 {
    font-size: 12px !important;
    color: black !important;
}

h3 {
    font-size: 10px !important;
    color: #000 !important;
}

a {
    font-size: 10px !important;
    color: #000 !important;
}

div.buy {
background: none !important;
height: 12px !important;
}

img.photo {
display: none !important;
}

table.catalog-table td {
padding: 2px !important;
}

div.content {
   background-color: #CFF8A6 !important;
}

div.menu ul li a {
    color: #FFFFFF !important;
}

td {
text-align: left!important;
}

}
