css:
@-moz-document url-prefix('https://docs.google.com/document'),url-prefix('http://docs.google.com/document') {
	
.kix-page-compact{
    border-top: 1px dotted #6d6861;
    background-color:#948F87 !important;
}

.kix-page>div,
.kix-page.kix-page-paginated{
    background-color:#948F87!important;
}
.kix-page.kix-page-compact-first{
    background-color:#948F87!important;
}
.kix-spelling-error{
    border-bottom:2px dotted #bcae98;
}
.kix-paginateddocumentplugin.kix-paginateddocumentplugin-compact-mode>.docstext-unprintable{
    background-color:#948F87!important;
}
#docs-editor{
    background-color:black!important;
}
#docs-toolbar-wrapper{
    background-color:#808080!important;
}
#docs-toolbar{
    background-color:#808080!important;
}

::-webkit-scrollbar-thumb{background-color:rgba(255,255,255,.5)!important;}
::-webkit-scrollbar-thumb:hover{
    background-color:rgba(255,255,255,.8)!important;
}


.kix-ruler-background{
    background-color:#000;
}
.kix-ruler-background-inner{
    background-color:#777;
}
.kix-ruler-face-number{
    color:#aaa;
}
#docs-chrome{
    background-color:#888;
}
.jfk-button-action{
    border:1px solid black;
    background-color:#888;
    background-image:none;
}

.kix-cursor-caret { 
        border-left-width: 1px;
	border-color: orange!important; 
	display: block !important;
	box-shadow: 0 0 10px orange, 0 0 20px orange, 0 0 40px orange;
}

}
