css:
@-moz-document domain("community.foxsportsnext.com"), domain('community1.foxsportsnext.com'), domain('community2.foxsportsnext.com'), domain('community3.foxsportsnext.com'), domain('community4.foxsportsnext.com'), domain('community5.foxsportsnext.com'), domain('community6.foxsportsnext.com'), domain('community7.foxsportsnext.com'), domain('community8.foxsportsnext.com'), domain('community9.foxsportsnext.com') {
/** Change this hex color to adjust the page background **/
body#lia-body { background-color:/*[[pagebgcolor]]*/ !important; }

/** This is the default message background color. I would suggest leaving this the same **/
.linear-message-list > div { background-color:#efefef !important; }

/** Change this hex color to change the alternating background color **/
.linear-message-list > div:nth-child(odd) { background-color:/*[[postbgcolor]]*/!important; }

/** This code handles all of the layout adjustments **/
#lia-body .lia-content .lia-panel-message > .lia-panel-message-content > .lia-decoration-border > .lia-decoration-border-content {padding:0 !important; }
.fblike { display:none !Important; }
#lia-body .lia-content ul.lia-list-standard, #lia-body .lia-content ol.lia-list-standard { margin-left:10px !important; }
#lia-body .lia-content .lia-quilt-layout-two-column-widemain-side .lia-quilt-column-main-content { width:100% !important; }
.lia-quilt-users-and-ad { display:none !important; }
#footer-login, #footer-news { display:none !important; }
.lia-quilt-row-footer { display:none !important; }
#lia-body .lia-content .lia-panel-message > .lia-panel-message-content > .lia-decoration-border > .lia-decoration-border-content { background-position:35px 0; }
#lia-body .lia-content .lia-quilt-row-forum-message-main .lia-quilt-column-main-right { padding: 10px 0 0 30px !important; }
#lia-body .lia-content .lia-quilt-layout-forum-message .lia-component-author { padding: 10px 0 0 10px !important; }
img.lia-user-rank-icon-left { display:none !Important; }
#lia-body .lia-content .lia-quilt-layout-two-column-main-side .lia-quilt-column-main-content { width:98% !important; padding:0 10px !important; }
#lia-body .lia-content .lia-quilt-layout-two-column-main-side .lia-quilt-column-side-content { display:none !important; }
#lia-body .lia-content table.lia-list-wide td { padding: 5px 10px !important; }
#lia-body.ForumPage .lia-content .lia-menu-bar-top { background-image:none !important; height:25px !important; padding: 3px 8px 0 0 !important; }
.lia-quilt-row-ads { display:none !important; }
#lia-body .lia-content .lia-component-users-action-registration, #lia-body .lia-content .registration-link, #lia-body .lia-content .login-link, #lia-body .lia-content .lia-component-users-action-login, #lia-body .lia-content .view-profile-link, #lia-body .lia-content .lia-component-users-action-view-user-profile, #lia-body .lia-content .lia-component-users-action-logout, #lia-body .lia-content .logout-link, #lia-body .lia-content .lia-component-common-widget-user-navigation li span.lia-component-common-widget-separator-character, #lia-body .lia-content .lia-component-common-widget-user-navigation li span.lia-component-common-widget-user-nav-separator-character { display:block !important; }
#lia-body .lia-content .lia-quilt-header { padding:5px !important; margin: 0 !important; }
#lia-body .MinimumWidthContainer .min-width-wrapper .min-width > .lia-content { box-shadow: 0 0 15px #333 !important; border-radius: 5px !important; margin: 0 0 15px !important; padding: 5px !important; width:90% !important; }
#lia-body .lia-content .lia-quilt-column-main-content .lia-quilt-column-alley { margin:0 !important; }
#lia-body .lia-content .lia-menu-bar { border-bottom:1px solid #333 !important; }
.drop-nav { box-shadow:0 5px 10px #000 !important; }
#threadnavigator { margin-right:5px; }
#mega-footer, #topbar-container { display:none !important; }
#lia-body .lia-content .lia-panel-message { background:none !important; }
#lia-body.ForumPage .lia-content table.lia-list-wide { margin:0 !important; }
h1, h2, h3, h4, h5 { text-transform:none !important; }
.logo-next { display:none !important; }
#main-nav .home { margin-left:20px !important; }
.topNav nav {height:35px !important; }
div#main-nav.logo-small ul div.drop-nav { left: 55px !important; }
#lia-body .lia-content .lia-quilt-column-common-header .lia-component-common-widget-search-form input.search-input { width:270px !important; }
.lia-user-avatar-message {width:auto !important; height:auto !important; max-width:120px !important; max-height:120px !important; }
h1.PageTitle, h1.PageTitle span { text-transform:capitalize !important; }
}
