css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filelist.ro"), url-prefix("http://flro.org") {
html, body{
background: url("http://filelist.ro/styles/images/header2.jpg") repeat scroll 0px 0px transparent!important;
}

.noborder img{
border-radius: 5px;
box-shadow: 0 0 5px #000;
}

.statusbar {
box-shadow: 0 0 10px #66CCFF;
border: 1px solid #22AADD !important;
background: -moz-linear-gradient(top,  #323232 0%, #121212 100%) !important;
}

.cblock-top{
display: none !important;
}

.cblock-content{
box-shadow: 0 0 7px #000;
border-radius:7px; 
background: #151515 !important;
margin:0 auto !important;
}

.cblock-bottom{
display: none !important;
}

.cblock-header{
z-index: 100;
background: url("http://filelist.ro/styles/images/content-head-black.png") repeat-x scroll 0px 0px transparent !important;
}

#nav ul li{padding:5px!important;padding-top:0!important}
#navigation{background:none!important;border:none!important;box-shadow:none!important}
#nav ul li a{background:none!important;padding:8px 15px 15px 15px!important;color:#fff!important;height:1px!important;text-shadow:0 1px #111;-moz-transition:height .3s!important;}
#nav ul li a:hover{color:#212121!important;height:21px!important;background:#333!important;background:-moz-linear-gradient(top,#434343 0,#323232 100%)!important;text-shadow:1px 1px 1px #000!important;border-radius:3px 3px 0 0 !important;box-shadow:0 -1px 2px rgba(0,0,0,0.75)!important;}
#nav ul li.active a{color:#454545!important;height:21px!important;background:#222!important;background:-moz-linear-gradient(top,#656565 0,#323232 18px)!important;text-shadow:1px 1px 1px #000!important;border-radius:3px 3px 0 0 !important;box-shadow:0 -1px 2px rgba(0,0,0,0.75)!important;}
#nav lihome{height:24px;background:-moz-linear-gradient(top,#4045D5 0,#1B1F96 100%)!important;text-shadow:1px 1px 1px #000!important;border-radius:3px 3px 0 0 !important;box-shadow:0 -1px 2px rgba(0,0,0,0.75)!important;}
FONT[face="century gothic"]{font-family:Verdana,sans-serif!important;font-size:11px!important;}
.mainheader{height:220px!important;background:-moz-linear-gradient(top,#454545 0,#202020 90%)!important}

.cblock-headerleft{
visibility: hidden;
}
.cblock-headerright{
visibility: hidden;
}
.cblock-header{
border-radius: 5px 5px 0 0;
border: 1px solid #000;
}
}
