css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("paizo.com") {
body, .boxHeader{
    color: #888! important; 
    background-color: #000 !important;
}
a:link{
    color: #0f0! important;
    text-shadow:0 0 .3ex #050;
}
a:visited{
    color: #090!important;
}
a:active{
    color: #0f0!important;
}
a:hover{
}
hr{
	color:#335 !important;
}
div.bb-title{
    background-color: transparent !important;
}
div.bb-frame{
    background-color: #011 !important;
    border-radius: 1ex;
}

p, td, tr, tbody, .boxHeaderBig, .browseLadderAncestors{
    color: #aaa! important; 
    background-color: transparent !important;
}
[bgcolor="cccccc"],[bgcolor="white"],[bgcolor="fff"]{
    background-color: #000 !important;
}
.bb-upperleft,.bb-top,.bb-upperright,.bb-left,.bb-bottomleft,.bb-bottom,.bb-bottomright,.bb-right{
    background-color: transparent !important;
    background-image: none !important;
}

}
