css:
@-moz-keyframes blinker {  
  from { 
        background:-moz-radial-gradient(center bottom, rgba(255,0,0,1), Transparent),
                   -moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75));
       }

  to   {
        background:-moz-radial-gradient(center bottom, rgba(255,0,0,.25), Transparent),
                   -moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75));}
       }


#main-window[tabsontop="true"][sizemode="normal"] #TabsToolbar {    

     margin-left: 95px !important; 

     margin-right: 80px !important;

     min-height: 26px !important;

}





#main-window[tabsontop="true"][sizemode="normal"] #toolbar-menubar { 

     margin-left: 95px !important; 

     margin-top: -26px !important; 

}



#main-window[tabsontop="true"][sizemode="normal"] #appmenu-button-container {

position: relative !important;

z-index: 2 !important;

}





scrollbar {
 -moz-appearance: none !important;
 background: transparent !important;
 }
 thumb,scrollbarbutton {
 -moz-appearance: none !important;
 position:absolute!important;
 border:1px solid rgba(0,0,0,.6)!important;
 border-radius:50px!important;
 min-width:5px!important;
 box-shadow:black 0px 0px 2px!important;
 background-color: rgba(0,0,0,.5) !important;
 }
 thumb:hover,scrollbarbutton:hover {
 -moz-appearance: none !important;
 background-color: rgba(0,0,0,.95) !important;
 }
 scrollbarbutton {
 display: none !important;
 }
 scrollbar[orient="vertical"] {
 min-width: 5px !important;
 }

#nav-bar{
background:-moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
border-top:groove 2px!important;
border-bottom:none!important;
border-color:rgba(0,0,0,.375)!important;
}
#tabbrowser-tabs{
border-top-left-radius:3px!important;
border-left:groove 2px!important;
border-color:rgba(0,0,0,.275)!important;
}
.tabbrowser-tab{
background:-moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
border:none!important;
border-top:groove 2px!important;
border-color:rgba(0,0,0,.375)!important;
}
.tabbrowser-tab[selected="true"]{
background: -moz-radial-gradient(center bottom , rgba(0,128,255,1), Transparent),
            -moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
}
.tabs-newtab-button{
background:-moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
border:none!important;
border-top:groove 2px!important;
border-right:ridge 2px!important;
border-color:rgba(0,0,0,.375)!important;
border-top-right-radius: 3px!important;
}
#appmenu-button{
background: -moz-radial-gradient(center top, rgba(0,128,255,.75), Transparent)!important;
border-color:rgba(0,128,255,.125)!important;
box-shadow:black 0px 0px 2px!important;
box-shadow:black 0px 0px 2px!important;
}
#appmenu-button:hover{
background: -moz-radial-gradient(center top, rgba(0,128,255,1), Transparent)!important;
border-color:rgba(0,128,255,.25)!important;
}
#appmenu-button[open="true"]{
background: -moz-radial-gradient(center top, rgba(0,128,255,.5), Transparent)!important;
border-color:rgba(0,128,255,.06125)!important;
}

.tabbrowser-tab[titlechanged]{
-moz-animation-name:blinker;
-moz-animation-iteration-count: infinite;  
-moz-animation-timing-function: cubic-bezier(.5, 0, 1, 1);
-moz-animation-duration: 1.7s;
background:-moz-radial-gradient(center bottom, rgba(255,0,0,1), Transparent),
           -moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
border:none!important;
border-top:groove 2px!important;
border-color:rgba(0,0,0,.375)!important;
}

#nav-bar toolbarbutton.toolbarbutton-1:hover[disabled="true"]{
background:-moz-linear-gradient(center top , #101010, #000000)!important;
border:solid 1px!important;
border-color:transparent!important;
border-radius:5px!important;
box-shadow: rgba(255,255,255,.375) 0px 1px 0px inset,
            rgba(0,0,0,.375) 0px -1px 0px inset!important;
}
#nav-bar toolbarbutton.toolbarbutton-1:hover{
background:-moz-linear-gradient(center top , #303030, #000000)!important;
border:solid 1px!important;
border-color:transparent!important;
border-radius:3px!important;
box-shadow: rgba(255,255,255,.375) 0px 1px 0px inset,
            rgba(0,0,0,.375) 0px -1px 1px inset!important;
}

#urlbar, .searchbar-textbox{
background:rgba(0,0,0,.25)!important;
color:rgba(255,255,255,.85)!important;
box-shadow: rgba(0,0,0,.75) 0px 0px 3px inset,
            rgba(255,255,255,.25)0px 1px 0px !important;
border-radius: 5px!important;
}
