css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mmo-champion.com"),
domain("blue.mmo-champion.com")
{

html, body, body.site-bluetracker
{
background: url(http://i.imgur.com/pazF1.png) !important;
background-repeat: repeat !important;
}
.pagination dl>dd span a
{
border: #222222 !important;
background: #111111 !important;
}
div.recent-body, div.recent, div.wysiwyg_block.blockbody.formcontrols
{
background: #222222 !important;
border: #333333 !important;
}
div.body_wrapper, div.article_preview, div.collapse.wgo_block.block, div.article_width, ul.popupbody
{
border: #222222 !important;
background: #111111 !important;
}
blockquote.postcontent.lastedited, ul.floatcontainer, div#postpagestats
{
color: white !important;
}
div.meta, p, h3.blocksubhead, div.navpopupmenu.popupmenu.nohovermenu, div.blockrow, span.shade, span.separator, h1, label, p.label, strong, a.understate, div.lastpostby, p.lastpostdate, span.time, span.viewing, h4, b, a.title, span.prefix.understate, a.title.threadtitle_unread, div.cms_widget_post_userinfo, h3.pollsubhead, div.cms_article_username, a.report, a.reply, dd, dt, span.usertitle, h2.title.icon, blockquote.postcontent.restore, li, h5
{
color: #ffffff !important;
}
div.rating0.nonsticky, div.forumrow, div.rating1.nonsticky, div.rating2.nonsticky, div.rating3.nonsticky, div.rating5.nonsticky, div.rating4.nonsticky, div.forumrow.table, div.wgo_subblock.section, form.forum_info_form.blockbody.formcontrols.floatcontainer
{
background: #222222 !important;
color: #000000 !important;
}
div.rating0.sticky, div.rating5.sticky, div.rating4.sticky, div.rating3.sticky, div.rating2.sticking, div.rating1.sticky
{
background: #333233 !important;
}
blockquote.posttext.restore
{
background: #222222 !important;
color: white !important;
}
div.bbcode_quote, div.postbithead, li.postbitlegacy.postbitim.postcontainer.old, li.postbitlegacy.postbitim.postcontainer.new, div.userinfo, div.postbody, div.content, div.textcontrols.floatcontainer, h2.title.icon, div.postdetails, div.wysiwyg_block, div.blockrow, span.cke_wrapper.cke_ltr, div.blockfoot.actionbuttons, form.block, div.blockbody, div.blockfoot, .blockrow
{
background: #333333 !important;
}

td, div.border
{
border: #222222 !important;
background: #444444 !important;
color: #ffffff !important;
}
span.highlight, div.userinfo_noavatar, div.postrow
{
background: #444444 !important;
}
table.sortable.news-table, li.postbitlegacy.postbitim.postcontainer.old, blockquote.signature.restore, div.content, div.postfoot, div.postbody
{
border: #111111 !important;
}
h2.forumtitle
{
color: #ffffff !important;
background: #222222 !important;
}
legend, p.description
{
color: #ffffff !important;
}
div.bbcode_postedby, div,block.formcontrols.settings_form_border, li.postbitlegacy.postbitim.postcontainer.new
{
border: none !important;
}
a.quickreply
{
background: url(http://i.imgur.com/1ShtF.png) no-repeat !important;
}
img[src="images/buttons/reply_40b.png"]
{
background: url(http://i.imgur.com/Tj6ye.png) no-repeat !important;
}
img[src="images/buttons/edit_40b.png"]
{
background: url(http://i.imgur.com/Tj6ye.png) no-repeat !important;
}
img[src="images/buttons/report-40b.png"]
{
background: url(http://i.imgur.com/Tj6ye.png) no-repeat !important;
}
div.article.cms_clear.restore.postcontainer
{
color: #999999 !important;
}
a.editpost
{
background: url(http://i.imgur.com/dbx0H.png) no-repeat !important;
}
a.newreply
{
background: url(http://i.imgur.com/fJY66.png) no-repeat !important;
}
a.multiquote
{
background: url(http://i.imgur.com/GhnZ8.png) no-repeat !important;
}
img[src="images/site_icons/message.png"], img[src="images/site_icons/forum.png"], img[src="images/cms/widget-forum.png"], img[src="images/cms/widget-article.png"], img[src="images/site_icons/account.png"], img[src="images/site_icons/profile.png"], img[src="images/site_icons/network.png"]
{
background: url(http://i.imgur.com/Tj6ye.png) !important;
}
a.report
{
background: url(http://i.imgur.com/FF2DJ.png) no-repeat !important;
}
div.cms_article_txt_content.postcontainer, span.label
{
color: #999999 !important;
}
div.block.mainblock, div.block, div.subsection, div.selected_view_section, div.infractionbit
{
border: none !important;
background: #333333 !important;
}
dd.userprof_module
{
background: #444444 !important;
}
dd.userprof_moduleinactive
{
background: #222222 !important;
}
div.title, div.header, li.postbit.blockbody, div.popupbody, div.toolsmenu, label, .popupbody a
{
border: #333333 !important;
background: #222222 !important;
}
ol#infractionslist.blockbody.settings_form_border,li.active
{
background: #333333 !important;
border: #222222 !important;
}
h2.blockhead, div.collapse.wgo_block.block, div.recentbar, h5, h2.searchlisthead, span.threadlisthead.table
{
border: none !important;
}
li.forumbit_nopost.new.L1, form, .recent div, li.forumbit_nopost.old.L1, form.forum_info_forum.blockbody.formcontrols.floatcontainer, .blockbody
{
border: none !important;
background-color: black !important;
}
div.forumhead.foruminfo.L1.collapse, div.threadlisthead, div.forumhead
{
margin: 0px !important;
box-shadow: none !important;
}
div.collapse.wgo_block.bloc
{
border: black !important;
}
span.ctrlcontainer
{
color: black !important;
}
label, div.options_block, form.pagination.popupmenu.nohovermenu, div.above_threadlist_controls.toolsmenu, div.thread_controls.toolsmenu, div.threadlist div, div.meta, div.cms_widget_post_userinfo
{
background: transparent !important;
}
a.textcontrol
{
color: black !important;
text-shadow: #ffffff 0.1em 0.1em 0.1em, white 0.1em -0.1em 0.1em, white -0.1em -0.1em 0.1em, white -0.1em 0.1em 0.1em !important;
}
div.bbcode_quote
{
border-radius: 10px !important;
background: #111111 !important;
}
div.message
{
padding-left: 10px !important;
border-radius: 10px 10px 10px 10px;
}
a.cke_button_vbColumnDelete.cke_disabled, a.cke_off.cke_button_subscript, a.cke_off.cke_button_superscript, a.cke_off.cke_button_horizontalrule
{
background: #999999 !important;
}
div.cms_widget_post_bit
{
background: #222222 !important;
}
a
{
color: white !important;
}
a.cke_button_enhancedsource.cke_on
{
padding-top: 2px !important;
padding-right: 4px !important;
width: 40px !important;
height: 20px !important;
border-width: 1px !important;
background: transparent !important;
}
div.border, div.block, form.block, li.postbitlegacy.postbitim.postcontainer.old, li.postbitlegacy.postbitim.postcontainer.new, div.posthead, div.textcontrols.floatcontainer
{
border-radius: 10px !important;
}
ol.posts.border
{
border: none !important;
background: #111111 !important;
}
ol.pmlist, li.blockrow.pmbit, h3.blockhead, fieldset.blockrow.surround
{
border-width: 0px !important;
}
hr
{
color: #111111 !important;
}
div.threadlisthead.table
{
border-radius: 0px !important;
padding-right: 4px !important;
background: #222822 !important;
}
div.border
{
border-radius: 00px !important;
}
div.article_preview
{
margin: 0px !important;
}
div.noinlinemod.forumfoot
{
background: #111111 !important;
}
form
{
background: transparent !important;
}
h4.blockhead
{
border: none !important;
box-shadow: none !important;
}
div#usercp_nav
{
background: #444444 !important;
}
div#usercp_nav div.block
{
margin: 4px !important;
width:180px !important;
}
div.blockfoot.controlbar.textpad a.textcontrol, h3.blockhead a.textcontrol
{
text-shadow: none !important;
color: white !important;
}
form.block
{
margin-top: 5px !important;
}
li.imodselector.threadbit.hot.new, div.collapse.wgo_block.block, li.imodselector.threadbit.new, li.imodselector.threadbit.dot.hot, li.imodselector.threadbit.hot.lock, li.imodselector.threadbit.hot
{
box-shadow: none !important;
}
pre.bbcode_code
{
border: #555555 !important;
background: #777777 !important;
}
li.inactive a:hover, li.active a:hover
{
background: #222222 !important;
}
li.ui-state-default.ui-corner-top a
{
background: #555555 !important;
}
}
