css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.diary.ru') {

body {
    color: rgb(51, 51, 51);
    font-family:Calibri;
    margin: 0px;
    padding: 0px;
background-color: #999999 !important;    
background-image: url(http://i45.tinypic.com/33cqc8g.jpg) !important; 
}

a {color:#4d3d6a !important; text-decoration:none !important;}
a:hover {color:#6d5d8a !important;}

.menu li ul {background-color: #e6e6e6 !important;}

#logo_a {width:53px !important;} 
#logo {margin-left:-10px !important;
width:53px !important;
margin-right:13px;
margin-top:5px;padding-right:10px !important;}

#top{padding-left: 190px !important;}

#logo_a img{padding-right: 53px !important;
height: 0 !important;
width: 0 !important;

padding-top: 51px !important;
background: url(http://i1151.photobucket.com/albums/o629/m0v/Capricorn1-2.png) !important;}

#dog {
height: 0 !important;
width: 0 !important;
padding-top: 90px !important;
background: none !important;}

#topwrap{margin:auto !important;

background-color: #3e3a74 !important;
background-image: url(http://i1151.photobucket.com/albums/o629/m0v/-9.jpg) !important;}

#main_container {margin-bottom:-25px !important;}

#contant{margin:auto !important;
width:900px !important;
background-image: url(http://i45.tinypic.com/33cqc8g.jpg) !important;
margin-top:-25px !important;
padding-left:10px;
margin-bottom:-15px !important;
}

.counterz, .advert {padding-top:10px !important;}

.wrapper {font-family:Calibri;} 

.wrapper a {
font-weight: bold;
color:#4d3d6a;
text-decoration:none;
border:none;
-webkit-transition: all 0.7s ease;
-moz-transition: all 0.7s ease;
-o-transition: all 0.7s ease;}

.wrapper a:hover {
color:#6d5d8a;
-webkit-transition: all 0.7s ease;
-moz-transition: all 0.7s ease;
-o-transition: all 0.7s ease;
border:none;}

.bottom_all {width:985px !important;
margin:auto !important;}

#bottom {margin:auto !important;
margin-top:-25px !important;
width:890px !important;
padding-left:10px !important;
padding-right:10px !important;
background-image: url(http://i45.tinypic.com/33cqc8g.jpg) !important;
}
#m_menu a {color:#6d5d8a !important; 
border-bottom:1px solid #6d5d8a !important;}

#inf_menu a {color:#82719f !important; }

#menu_box {
margin:auto !important;


display:block !important;
background-color: #e6e6e6 !important;
background-image: url(http://i1151.photobucket.com/albums/o629/m0v/line_menu-1.jpg) !important;}

.menu li a, .menu li b {background:none !important;}
.menu ul li a {color:#4d3d6a !important;}
.menu ul li a:hover {color:#6d5d8a !important;}

.line, 
.line_h{
margin:auto !important;
width:900px !important;
padding-left:-10px;
background: url(http://i1151.photobucket.com/albums/o629/m0v/05351.gif) repeat-x !important;}

.box_main_news {width:20% !important;
}

table {border-color:#3f3f3f !important;
padding:10px !important;
width:890px !important;}

table.table tr:hover td{
background-color: #f6f6f6 !important;}

table.table th {color:#343434 !important; 
background: url(http://i1151.photobucket.com/albums/o629/m0v/05351.gif) repeat-x bottom left !important;}

table.table th a {
color: #343434 !important;}

table.table th a:hover {
color: #5a5a5a !important;}

.pages a {color: #343434 !important;}
.pages a:hover {
color: #5a5a5a !important;}

h1 {font-family:tahoma;
color:#515151 !important;
padding-left:15px !important;


}

h2 {font-family:tahoma;
color:#515151 !important;
padding-left:15px !important;
padding-top:5px !important;

}

fieldset.box, div.box, form.box {background-color:#fff !important;}
.postDate {background-color:#f6f6f6  !important;background-image: url(http://i49.tinypic.com/2a5dd2w.jpg) !important;}

.singlePost {background: #f6f6f6 !important;
width:878px  !important;
background-image: url(http://i49.tinypic.com/2a5dd2w.jpg) !important;
padding:5px;
border: none !important;}

.singlePost img {max-width:695px  !important;
}

#msg_form  {width:600px !important;}

.postLinksBackg, .postLinksBackg a {color: #343434 !important;}

.ser {color: #e6e6e6  !important;}

fieldset.box, div.box, form.box, table.box {
background: #e6e6e6 !important; border:none !important;}

h6 {color: #3b3b3b  !important;}

.sml_div {
background-color: #e6e6e6 !important;}

#avatarbit #avatar {background-color: #f6f6f6 !important;
border:none !important;}

table.box, .sml_box, #phototdw, #phototdw a, #avatarbit  {
background: #e6e6e6 !important;
border: none !important;}

#box_options_content{width:850px !important; margin:auto !important; padding-left:10px; margin-left:15px !important; padding-top:5px;}


}
