/* Created by Ramon Rosin */
@-moz-document domain("shack-software.de"){
::selection{
    color: #333333 !important;
    background-color: #D8D8D6 !important;
}

body{
    background-color: #333333 !important;
    color: #f6f6f6 !important;
}

#user_panel{
    border-bottom: 1px solid #4A4A4A !important;
    background-color: #808080 !important;
}

#user_panel a{
    color: #f6f6f6 !important;
}

#site_header{
    border-bottom: 1px solid #C0C0C0 !important;
}

h1:hover{
    color: #E10000 !important;
    text-shadow: 0px 0px 8px #f6f6f6 !important;
    -o-text-shadow: 0px 0px 8px #f6f6f6 !important;
    -moz-text-shadow: 0px 0px 8px #f6f6f6 !important;
    -ms-text-shadow: 0px 0px 8px #f6f6f6 !important;
    -webkit-text-shadow: 0px 0px 8px #f6f6f6 !important;
    -khtml-text-shadow: 0px 0px 8px #f6f6f6 !important;
    
    filter: dropshadow(color=#f6f6f6, offx=0, offy=0) !important;
    -o-filter: dropshadow(color=#f6f6f6, offx=0, offy=0) !important;
    -moz-filter: dropshadow(color=#f6f6f6, offx=0, offy=0) !important;
    -ms-filter: dropshadow(color=#f6f6f6, offx=0, offy=0) !important;
    -webkit-filter: dropshadow(color=#f6f6f6, offx=0, offy=0) !important;
    -html-filter: dropshadow(color=#f6f6f6, offx=0, offy=0) !important;
}

#site_header a{
    color: #f6f6f6 !important;
}

#site_header span{
    color: #f6f6f6 !important;
}

#site_header a:hover{
    color: #ffffff !important;
}

#site_section{
    border-top : 1px solid #C0C0C0 !important;
}

#site_section a{
    color: #f6f6f6 !important;
}

#site_section a:hover{
    color: #FF0000 !important;
}

#site_footer{
    border-top : 1px solid #C0C0C0 !important;
}

#site_footer a{
    color: #f6f6f6 !important;
}

#site_footer a:hover{
    color: #FF0000 !important;
}

hr{
    border-top: 1px solid #CCCCCC !important;
}

#navSquare a:link, #navSquare a:visited {
        color: #f6f6f6 !important;
}

#navSquare a:link.active, #navSquare a:visited.active, #navSquare a:hover{
    color: #f6f6f6 !important;
}

.submit{
    background-color: #f6f6f6 !important;
    color: #333333 !important;
}

.submit:hover{
    color: #FF0000 !important;
}

.disabled_submit{
    background-color: #C0C0C0 !important;
    color: #FFFFFF !important;
}

.navi{
    background: #333333 !important;
}

.logo_shadow{
    border: 1px solid #808080 !important;
    
    -webkit-box-shadow: 0px 10px 30px 0px #808080 !important;
    -o-box-shadow: 0px 0px 30px 0px #808080 !important;
    -moz-box-shadow: 0px 10px 30px 0px #808080 !important;
    -ms-box-shadow: 0px 10px 30px 0px #808080 !important;
    -khtml-box-shadow: 0px 10px 30px 0px #808080 !important;
    box-shadow: 0px 10px 30px 0px #808080 !important;
}

.table{
    border: 1px solid #C0C0C0 !important;
}

.table_header{
    background-color: #2C95D6 !important;
}

.warnbox
{
    background-color: #BFA743 !important;
    color: #f6f6f6 !important;
    border: 1px solid #937809 !important;
}

.warnbox a
{
    color:#f6f6f6 !important;
}

/* Warnung */
.redbox {
    background-color: #7E0000 !important;
    color: #f6f6f6 !important;
    border: 1px solid #480000 !important;
}

.redbox a {
    color:#333333 !important;
}

/* Entwicklung */
.devbox
{
    background-color: #BFA743 !important;
    color: #f6f6f6 !important;
    border: 1px solid #937809 !important;
}

.devbox a
{
    color:#f6f6f6 !important !important;
}

.devbox a:hover
{
    color: #FF0000 !important;
}

/* Download */
.dlbox
{
    background-color: #93C25A !important;
    color: #333333 !important;
    border: 1px solid #779162 !important;
}

.dlbox a
{
    color: #333333 !important;
}

/* Info */
.infobox {
    background-color: #2C95D6 !important;
    color: #f6f6f6 !important;
    border: 1px solid #005182 !important;
}

.infobox a {
    color: #f6f6f6 !important;
}

.infobox a:hover {
    color: #FF0000 !important;
}

.infobox .timestamp{
    color: #C0C0C0 !important;
}

.greenbox
{
    background-color: #427D00 !important;
    color: #f6f6f6 !important;
    border: 1px solid #1A7200 !important;
}

.greenbox a
{
    color: #f6f6f6 !important;
}

.table{
    border: 1px solid #C0C0C0 !important;
}

.tabel_header{
    background-color: #2C95D6 !important;
}
}

