css:
@-moz-document domain("docs.google.com"), domain("drive.google.com") {
/* Header */
#docs-chrome {
    background-color: #d0dada !important;
}

/* Main body of the editor */
.kix-page {
    background-color: #dadada !important;
    border-color: #111 !important;
    box-shadow: 0 0 0 !important;
}

/* Borders around the editor */
#docs-editor {
    background-color: #001 !important;
}
}

@-moz-document domain("docs.google.com"), domain("drive.google.com") {
/* Header */
#doclist {
    background-color: #f00 !important;
}
}

@-moz-document domain("docs.google.com"), domain("drive.google.com") {
/* Scrollbar, it's too dark otherwise */
::-webkit-scrollbar-thumb {
	background: #339 !important;
}
}
