@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nuzl.com") {

body {
background: #E3E0CF none center top repeat !important;
color:#555555 !important;
line-height:1.6 !important;
}

#wrap {
background:white !important;
margin:75px auto !important;
border:2px solid #DCCFC8 !important;
}

#headcontent, #content, #paging, #footcontent {
margin:0pt auto !important;
position:relative !important;

}

#header {
background:#6D1D7C !important;
color:#90A7C6 !important;
font-family: sans,'Lucida Grande',Arial,sans-serif !important;
font-size:1.3em !important;
font-weight: bold !important;
text-transform:capitalize !important;
}

ul#nav li a, #head-title a { 
display:block !important; 
padding:10px !important; 
border-right:1px solid #3D0845 !important; 
color:#fff !important; 
text-decoration:none !important;
 }

ul#nav li a:hover, #head-title a:hover { 
background:#823490 !important; 
color:#fff !important;
 }

ul#nav li.tab-home a{ 
color: #823490 !important;
}

ul#nav li.tab-rss { 
border-left:1px solid #3D0845 !important; 
float:right !important; 
}

ul#nav li.tab-rss a { 
color:#ffffff !important; 
}

ul#nav li.tab-rss a:hover { 
background:#944EC2 !important; 
color:white !important; 
}

ul#nav li span { 
display:none !important; 
}

.article {
font-family: "-ìœ¤ê³ ë”•330" !important;
}

.article a:link, .actionTrail a:link { 
color: #62226F !important;
text-decoration:none !important;
padding: 3px !important;
}

.article a:hover, .actionTrail a:hover {
color: #62226F !important;
background: #E9D6ED !important;
text-decoration:none !important;
-moz-border-radius-bottomleft:3px !important ;
-moz-border-radius-bottomright:3px !important;
-moz-border-radius-topleft:3px !important;
-moz-border-radius-topright:3px !important;
padding: 3px !important;
} 

.article a img {
background: #ffffff !important;
padding: 0px !important;
-moz-border-radius-bottomleft:3px !important ;
-moz-border-radius-bottomright:3px !important;
-moz-border-radius-topleft:3px !important;
-moz-border-radius-topright:3px !important;
}

.entry h1 a:hover{
background: #E9D6ED !important;
color: #62226F !important;

}

.microlog .article{
border-left:8px solid #eeeeee !important;
padding-left:15px !important;
}

#paging a:hover {
background:#944EC2 !important; 
color:white !important;
border:1px solid  #6D1D7C !important;
}


#footer {
background:#6D1D7C !important;
}

.foot-search {
background:white !important;
border:1px solid  #DCADD7 !important;
}
#footer .right {
font-size: 10px !important;
margin-left: 20px !important;
}


}
