/*

 * Edit this file and copy it as userContent.css into your

 * profile-directory/chrome/

 */



/*

 * This file can be used to apply a style to all web pages you view

 * Rules without !important are overruled by author rules if the

 * author sets any. Rules with !important overrule author rules.

 * http://userstyles.org/users/82707

 */



/*

 * For more examples see http://www.mozilla.org/unix/customizing.html

 */

 
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); /* set default namespace to XUL */

#appmenu-button .button-menu-dropmarker {
	list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAAO0lEQVR4AWMgGnz//v3o379//6Dj379/30dR+OfPn0//kcDPnz9vYzURqPsHSAFIA16rf/369YyBZgAAx2Y+f6eC6EYAAAAASUVORK5CYII=") !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button > .button-box > .box-inherit > .button-icon {
	list-style-image: url("data:image/png;base64,") !important}
#appmenu-button {
	background: #1979CA !important; /* Pale Moon */
	background: #034E99 !important; /* Aurora */
	background: #16314C !important; /* Nightly */
	background: #EE600A !important; /* Firefox */
	border-radius: 0 !important;
	border: 0 !important;
	box-shadow: none !important;
	text-shadow: none !important}
#main-window:-moz-window-inactive #appmenu-button:not(:hover) {
	background: rgba(0,0,0,.2) !important}
#appmenu-button:hover:not(:active):not([open]) {
	background: #2A8EE0 !important; /* Pale Moon */
	background: #035BB2 !important; /* Aurora */
	background: #224A72 !important; /* Nightly */
	background: #FF6600 !important; /* Firefox */
	transition: .3s ease-in !important}
#appmenu-button:hover:active,
#appmenu-button[open] {
	background: #1268B3 !important; /* Pale Moon */
	background: #043D70 !important; /* Aurora */
	background: #0B1826 !important; /* Nightly */
	background: #BB5000 !important; /* Firefox */
	-moz-transition: .2s ease-out !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button {
	background: #6F11B2 !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button:hover:not(:active):not([open]) {
	background: #832FBF !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button:hover:active,
#main-window[privatebrowsingmode=temporary] #appmenu-button[open] {
	background: #5D0999 !important}
#main-window[sizemode=maximized] #appmenu-button-container {
	-moz-margin-start: 3px !important}
