css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(pinterest.com) {

* {
 text-decoration: none!important;
 text-shadow: none!important;
}

html, body, #Header, #CategoriesBar, .feed.pin, #CategoriesDropdown, .submenu ul, #zoom, #ContextBar, .repins, .info, #Navigation li ul, #profile #ContextBar ul, .overlay, #Repin2 .PinForm, header#top div#lead, header#top, .ProfileInfo, .ProfileRepins {
 background: #000!important;
}

.FancyContainer span {
 background: none!important;
}

#profile #ContextBar ul {
 height: 50px!important;
}

#Navigation li ul {
 margin-top: -3px!important;
}

#zoomScroll, .write.convo.clearfix, .description, .BoardList li:first-child, .ui-widget-content, .WhiteContainer.clearfix, .header.lg {
 background: rgba(0,0,0,.9)!important;
}

div#caroMaskLeft, div#caroMaskRight, article header nav div.tags, article header nav, #UnauthCallout .Sheet {
 background: #000!important;
 opacity: .9!important;
}

.modal.wide.PaddingLess p {
 background: #000!important;
 color: #fff!important;
}

header#top a {
 margin-top: 0px!important;
}

.pinBoard h3 {
 height: 30px!important;
}

article header {
 height: 108px!important;
}

article {
 background: #000!important;
 color: #fff!important;
 margin-top: 50px!important;
}

#Header {
 position: fixed!important;
}

#CategoriesBar {
 position: relative!important;
 margin-top: 2px!important;
}

#CategoriesBarPage {
 position: relative!important;
}

a, span, button, h2, p, h3, h1, h4, #legal ol li, .name {
 color: #fff!important;
}

h2, h3, h1, h4, #AboutRight #legal p strong, #AboutRight #legal ol strong, h1 a {
 text-decoration: none!important;
 text-shadow: none!important;
 color: #ff0!important;
 font-size: 2em!important;
}

.InputArea ul li textarea, .InputArea, .CreateBoard input, .BoardList .wrapper {
 color: #000!important;
}

a:hover, span:hover {
 color: #ff0!important;
}

.PinImage.ImgLink, .PinForm .CharacterCount, .BoardPicker .CurrentBoard, .BoardPicker .CurrentCategory {
 color: #222!important;
}

.pin, .PinForm {
 background: rgba(100,100,100,.15)!important;
 opacity: .8!important;
}

.pin:hover, #PinImageHolder a img {
 background: rgba(100,100,100,.35)!important;
 opacity: 1!important;
}

#NewIndicator {
 margin-left: 6px!important;
}

.actions {
 opacity: 0!important;
}

.actions:hover {
 opacity: 1!important;
}

em, .convo.attribution.clearfix, .comment.convo.clearfix, .PlusIcon, .nav span, .nav img {
 display: none!important;
}

#ScrollToTop {
 margin-bottom: 20px!important;
}

#BoardThumbs li a img, #OriginThumbs li img, #SourceThumbs li img, #BoardThumbs li a img, #OriginThumbs li img, #SourceThumbs li img, .pinBoard .thumbs, .PersonPins img {
 position: relative!important;
 transform: scale(1, 1)!important;
 transition: all ease 1s!important;
 opacity: 1!important;
 z-index: 999!important;
}

#BoardThumbs li a img:hover, #OriginThumbs li img:hover, #SourceThumbs li img:hover, #BoardThumbs li a img:hover, #OriginThumbs li img:hover, #SourceThumbs li img:hover, .pinBoard .thumbs:hover, .PersonPins img:hover {
 position: relative!important;
 transform: scale(1.5, 1.5)!important;
 transition: all ease 1s!important;
 opacity: 1!important;
}

.feed .story .profile img {
 transform: scale(1, 1)!important;
 transition: all ease 1s!important;
 z-index: 2!important;
}

.feed .story .profile img:hover {
 transform: scale(2.5, 2.5)!important;
 transition: all ease 1s!important;
}

.PersonPins {
 height: 100px!important;
 width: 300px!important;
}

#ProfileHeader {
 margin-top: 40px!important;
}

.ProfileInfo, .ProfileRepins {
 margin-top: 35px!important;
}

.feed.pin {
 width: 200px!important;
}

/* Button shape ______________________________________*/

/* .Button.RedButton.Button18,
 .Button.WhiteButton.Button18,*/
 [class*="Button18"],

 #NewIndicator,
 .fb.login_button,

/* .Button.Button11.WhiteButton.ContrastButton.repin_link,
 .Button.Button11.RedButton.followuserbutton,
 .Button.WhiteButton.ContrastButton.Button11.likebutton,
 .Button.Button11.WhiteButton.ContrastButton.comment,*/
 [class*="Button11"],
/* .Button.Button13.RedButton.followuserbutton,
 .Button.Button13.RedButton.disabled.unfollowuserbutton.clickable,
 .Button.Button13.RedButton.followbutton,
 .Button.Button13.WhiteButton.ContrastButton.RepinButton,
 .Button.WhiteButton.ContrastButton.Button13.ZoomLikeButton,*/
 [class*="Button13"],
 #ScrollToTop,
 .SubmenuColumn a,
 .submenu ul li a,

 #PinEmbed,
 #PinReport,
 #EmailShare,

 #Navigation li ul li a,
 .PinForm .Buttons .facebook

 {
 display: inline-block!important;
 cursor: pointer!important;
 text-align: center!important;
 font: 12px/100% Arial, Helvetica, sans-serif!important;
 padding: .5em .8em .55em!important;
 text-shadow: 0 1px 1px rgba(0,0,0,.3)!important;
 border-radius: 0px 8em 0px 8em!important;
 box-shadow: 0 1px 2px rgba(255,255,255,.2)!important;
}

#CategoriesDropdown, #CategoriesBar li ul {
 margin-top: -4px!important;
}

.nav {
 display: inline-block!important;
 cursor: pointer!important;
 text-align: center!important;
 font: 12px/220% Arial, Helvetica, sans-serif!important;
 padding: .1em 1.3em .1em!important;
 text-shadow: 0 1px 1px rgba(0,0,0,.3)!important;
 border-radius: 0px 2em 0px 2em!important;
 box-shadow: 0 1px 2px rgba(255,255,255,.2)!important;
}

.FixedContainer .selected {
 display: inline-block!important;
 text-align: center!important;
 font: 12px/425% Arial, Helvetica, sans-serif!important;
 text-shadow: 0 1px 1px rgba(0,0,0,.3)!important;
 box-shadow: 0 1px 2px rgba(255,255,255,.2)!important;
}

/* Red buttons ______________________________________*/

 .Button.RedButton.Button18,

 .Button.Button11.WhiteButton.ContrastButton.repin_link,

 #PinReport, #UserNav a,


 .Button.disabled.clickable,
 .Button.Button13.RedButton.disabled,
 .Button.Button13.RedButton.disabled.unfollowuserbutton.clickable,
 .Button.WhiteButton.Button13.disabled.clickable.unlike_pin,
 .Button.Button13.WhiteButton.disabled.clickable.unfollowbutton,
 .Button.Button13.WhiteButton.disabled.clickable.unfollowuserbutton,
[class*="disabled"],

 #OpenCreateBoard,
 [value="Cancel"]

 {
 background: -moz-linear-gradient(top, #b20002 0%, #3d0001 100%)!important;
 color: #fff!important;
}

 .Button.RedButton.Button18:hover,

 .Button.Button11.WhiteButton.ContrastButton.repin_link:hover,

 #PinReport:hover,
 #UserNav a:hover,

 .Button.Button13.RedButton.disabled:hover,
 .Button.Button13.RedButton.disabled.unfollowuserbutton.clickable:hover,
 .Button.WhiteButton.Button13.disabled.clickable.unlike_pin:hover,
 .Button.Button13.WhiteButton.disabled.clickable.unfollowbutton:hover,
 .Button.Button13.WhiteButton.disabled.clickable.unfollowuserbutton:hover,
 .Button.disabled.clickable:hover,
[class*="disabled"]:hover,
 #OpenCreateBoard:hover,
 [value="Cancel"]:hover
 {
 background: -moz-linear-gradient(top, #3d0001 0%, #b20002 100%)!important;
}

/* Green buttons ____________________________________*/

 .Button.WhiteButton.Button18,

 #NewIndicator,
 #ScrollToTop,

/* .Button.WhiteButton.ContrastButton.Button11.likebutton,
 .Button.Button13.WhiteButton.ContrastButton.RepinButton,*/
[class*="ContrastButton"],

/* .Button.Button11.RedButton.followuserbutton,
 .Button.Button13.RedButton.followuserbutton,
 .Button.Button13.RedButton.followbutton,
 .Button.Button13.WhiteButton.followuserbutton,
 .Button.Button13.WhiteButton.followbutton.InlineButton,*/

[class*="follow"],

 #EmailShare,
 .FixedContainer .selected,
 .follow,

 .PriceContainer strong,
 [href="#"],
 .nav.selected.PinForm .Buttons .facebook,
 .followBoard a,

 #profile #ContextBar ul li a,
 ul.positions a,
 [value="Apply Now"]
 {
 background: -moz-linear-gradient(top, #34af00 0%, #0b3d00 100%)!important;
 color: #fff!important;
}

 #NewIndicator:hover,
 #ScrollToTop:hover,
 #EmailShare:hover,
 .FixedContainer .selected:hover,
 .follow li a:hover,

 .Button.WhiteButton.Button18:hover,

/* .Button.WhiteButton.ContrastButton.Button11.likebutton:hover,
 .Button.Button13.WhiteButton.ContrastButton.RepinButton:hover,*/
[class*="ContrastButton"]:hover,

/* .Button.Button11.RedButton.followuserbutton:hover,
 .Button.Button13.WhiteButton.followbutton.InlineButton:hover,
 .Button.Button13.WhiteButton.followuserbutton:hover,
 .Button.Button13.RedButton.followuserbutton:hover,
 .Button.Button13.RedButton.followbutton:hover,*/

[class*="follow"]:hover,

 .PriceContainer strong:hover,
 [href="#"]:hover,
 .nav.selected:hover,
 .PinForm .Buttons .facebook:hover,

 #profile #ContextBar ul li a:hover,
 ul.positions a:hover,
 [value="Apply Now"]:hover

 {
 background: -moz-linear-gradient(top, #0b3d00 0%, #34af00 100%)!important;
 color: #ff0!important;
 box-shadow: 0 1px 2px rgba(0,0,0,1)!important;
}

.Button.WhiteButton.Button18:active, #NewIndicator:active, .Button.Button11.RedButton.followuserbutton:active, .Button.WhiteButton.ContrastButton.Button11.likebutton:active, .Button.Button13.RedButton.followuserbutton:active, .Button.Button13.RedButton.followbutton:active, #EmailShare:active, .Button.Button13.WhiteButton.ContrastButton RepinButton:active, .follow li a:active, [href="#"]:active, #CategoriesBar .nav:active {

}

/* Blue buttons ____________________________________*/

.Button.Button11.WhiteButton.ContrastButton.comment, .fb.login_button, .SubmenuColumn a, .submenu ul li a, #PinEmbed, .Button.WhiteButton.ContrastButton.Button13.ZoomLikeButton, .links a, [href="/about/"], #Navigation li ul li a, .nav, .Button.Button13.WhiteButton, #OpenUploadPin, #boardName, [value="Forward Position"], nav#pagination a

 {
 display: inline-block!important;
 background: -moz-linear-gradient(top, #000cb5 0%, #00063d 100%)!important;
 color: #fff!important;
}

.Button.Button11.WhiteButton.ContrastButton.comment:hover, .fb.login_button:hover, .SubmenuColumn a:hover, .submenu ul li a:hover, #PinEmbed:hover, #Navigation li ul li a:hover, .Button.WhiteButton.ContrastButton.Button13.ZoomLikeButton:hover, .links a:hover, [href="/about/"]:hover, .nav:hover, .Button.Button13.WhiteButton:hover, #OpenUploadPin:hover, #boardName:hover, #resumator-forwardbutton-bx3dbA:hover, #resumator-forwardbutton-6pMLsT:hover, #resumator-forwardbutton-rPMRDk:hover, #resumator-forwardbutton-Eg0bNP:hover, #resumator-forwardbutton-2MTMME:hover, #resumator-forwardbutton-BHnRCb:hover, #resumator-forwardbutton-wpHpGs:hover, nav#pagination a:hover {
 background: -moz-linear-gradient(top, #00063d 0%, #000cb5 100%)!important;
 color: #ff0!important;
 box-shadow: 0 1px 2px rgba(0,0,0,1)!important;
}

.Button.Button11.WhiteButton.ContrastButton.comment:active, [href="/about/"]:active, #Navigation li ul li a:active {

}

/* Ignore button ________________________________________*/
.RemoveStory.ignorerecommendationbutton {
 display: inline-block!important;
 background: -moz-linear-gradient(top, #000cb5 0%, #00063d 100%)!important;
 border-radius: 8em!important;
 color: #fff!important;
}


.RemoveStory.ignorerecommendationbutton:hover {
 background: -moz-linear-gradient(top, #00063d 0%, #000cb5 100%)!important;
 color: #ff0!important;
 box-shadow: 0 1px 2px rgba(0,0,0,1)!important;
}

#PinEmbed:after {
 content: " stuff."!important;
}

#PinReport:after {
 content: " stuff."!important;
}

#EmailShare:after {
 content: " stuff."!important;
}


/* Lt Blue buttons ____________________________________*/

.tw.login_button, #SocialShare iframe { /* Twitter button */
 background: -moz-linear-gradient(top, #00b5b2 0%, #003d3d 100%)!important;
}

.tw.login_button:hover { /* Twitter button */
 background: -moz-linear-gradient(top, #003d3d 0%, #00b5b2 100%)!important;
}









}
