css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myanonamouse.net") {



#header, #hrnews, .lbc, .bHi, .bHis, .blockFoot, .xheadRow, #millionInfo
{display:none;}

#mainLeft, div.lbc { width:100%; margin: 0; }
#mainLeft, .lbc:first-child, .blockHeadCon b { display:block; }

.mainRight { float:left; }

#mainLeft div.blockBodyCon a, #mainLeft div.blockBodyCon div { display:inline; padding: 0 15px }

#mainLeft div.blockBodyCon .cen, div.blockBodyCon br { display:none; }

h1 { font-size: 18px; font-weight:normal; }

.blockHead { background: none !important; border-top:none !important; border-left:none !important; border-right:none !important; height:auto !important; min-height:0 !important; margin: 0 5px; width:auto !important; padding: 2px 4px; border-color: #99b !important; }

.blockBodyCon { padding: 0 5px 5px 5px !important; }

.blockBodyCon table { border:1px solid #99b; border-top:none; }

.blockHeadCon {position: static !important;}

/* Clean up some user input descriptions */
#descr * { font-family: Verdana,Helvetica,sans-serif !important; text-align: left !important; font-size: 12px !important; color:#000 !important;  }
#descr hr {max-width: 800px;}
#descr br { display:block; }

div.blockCon { margin-top:0; }
div.blockBody { border: 0; }

div.headRow { min-width: 930px; max-width: 100%; }

.row1 img { max-height: 200px; }

#mainBody form div:first-child {height:auto !important;}


}
