css:
@-moz-document domain("boards.4chan.org") {

body {
    background: url('http://desmond.imageshack.us/Himg822/scaled.php?server=822&filename=stripe7a87a71f1e29af87b.png&res=medium') !important;
    color: #fff !important;
    font: 13px Terminus !important;
    margin: 0px auto !important;
    padding: 0;
    }
form {
    border-left: 0px;
    border-right: 0px;
    margin: 0px auto !important;
    max-width: 95% !important;
    padding: 0px 5px 1px 5px;
    width: 95% !important;
    }
tbody td {
    background: #222222 !important;
    color: #ffffff !important;
    border: 0px solid #777777 !important;
    }
div.thread {
    background: #101010 !important;
    margin-bottom: 20px !important;
    margin-top: 10px !important;
    }
.rules {
    display: none !important;
    }
div.post.reply {
    background: #222222 !important;
    border: 0px !important;
    }
div.pagelist {
    background: #101010 !important;
    color: #ffffff !important;
    border-left: 0px;
    border-right: 0px;
    margin: 0px auto !important;
    border: 0px !important;
    }
a {
    color: #ffff00 !important;
    text-decoration: none !important;
    }
a:hover {
    color: #ffffff !important;
    }
hr {
    visibility: hidden !important;
    position: absolute !important;
    }
#absbot {
    visibility: hidden !important;
    position: absolute !important;
    }

#boardNavDesktopFoot {
    visibility: hidden !important;
    height: 0px !important;
    position: absolute !important;
    }
#boardNavDesktop {
    visibility: hidden !important;
    height: 0px !important;
    position: absolute !important;
    }
div.deleteform {
    visibility: hidden !important;
    height: 0px !important;
    position: absolute !important;
    }
div.stylechanger {
    visibility: hidden !important;
    position: absolute !important;
    }
div.boardBanner {
    visibility: hidden !important;
    position: absolute !important;
    }
strong {
    color: #ff0000 !important;
    }
div.postContainer {
    background: #222222 !important; 
    border-top: 5px solid #101010 !important;
    }
.opContainer {
    background: #222222 !important;
    }
.postingMode {
    background: #101010 !important;
    }

.sideArrows {
    visibility: hidden !important;
    position: absolute !important;
    }
.abbr {
    color: #ffffff !important;
    }
.fileThumb {
    margin: 20px !important;
    }
.post.op {
    color: #ffffff !important;
    }
.name {
    color: #ffac00 !important;
    }
.postertrip {
    color: #ff2222 !important;
    }

.subject {
    color: #ffff00 !important;
    }
.quote {
    color: #a9c938 !important;
    }
.postInfo a {
    color: #8888ff !important;
    }
.postInfo a:hover {
    color: #ffffff !important;
    }
input, input[type="submit"], input[type="button"], select, button, textarea {
    background: #000 !important;
    color: #aaa !important;
    }

input[type="checkbox"] {
    background: rgba(0,0,0,.4) !important;
    border: 1px solid rgba(255,255,255,.3) !important;
    border-radius: 0px !important;
    height: 10px !important;
    width: 10px !important;
    }
input[type="checkbox"]:checked {
    background: rgba(255,255,255,.4) !important;
    }
img#recaptcha_reload {
    background-image: url(data:image/png;base64,) !important;
    border: none !important;
    height: 0px !important;
    margin: 0 !important;
    padding-top: 18px !important;
    width: 25px !important;
    }
img#recaptcha_switch_audio {
    background-image: url(data:image/png;base64,) !important;
    border: none !important;
    height: 0px !important;
    margin: 0 !important;
    padding-top: 15px !important;
    width: 25px !important;
    }
img#recaptcha_whatsthis {
    background-image: url(data:image/png;base64,) !important;
    border: none !important;
    height: 0px !important;
    margin: 0 !important;
    padding-top: 16px !important;
    width: 25px !important;
    }
#recaptcha_logo, #recaptcha_tagline {
    display: none !important;
    }
/* Code blocks */
.prettyprint {
    color: #ffffff !important;
    background: #101010 !important;
    border: 1px solid #444444 !important;
    }
.pln {
    color: #ffffff !important;
    }
.kwd {
    color: #ff0000 !important;
    }
.pun {
    color: #ffffff !important;
    }
.lit {
    color: #ffaa00 !important;
    }
.str {
    color: #ffff00 !important;
    }
.typ {
    color: #00ffff !important;
    }
.com {
    color: #ffaa00 !important;
    }
.tag {
    color: #00ffff !important;
    }
.atn {
    color: #ff0000 !important;
    }
.atv {
    color: #ffff00 !important;
    }
/*Mobile Clean Up*/
div#boardNavMobile {
    margin: 0px !important;
    background-color: #101010 !important;
    border: 0px !important;
    }
.postInfoM {
    background-color: #101010 !important;
    border: 0px !important;    
    }
.postLink {
    background-color: #101010 !important;
    border: 0px !important;      
    }
div.postContainer {
    border: 0px !important; 
    }
#toggleMsgBtn, #threadWatcher, #twHeader, .extButton, .threadHideButton, hr, a[href*="jlist"], div#absbot {
    display: none !important;
    }
div.replyContainer {
    margin: 0px !important;
    }
a.mobilePostFormToggle {
    width: 95% !important;
    }
.mobile.mobilePostFormToggle.button {
    margin: 30px auto 0px !important;
    }
#globalToggle {
    margin: 0px auto !important;
    width: 90% !important;
    }
div.postLink {
    padding: 3px !important;
    }
.mPagelist {
    margin: 20px auto !important;
    border: 0px !important;
    }
a[href*="javascript:toggleMobilePostForm(0, 1);"] {
    display: none !important;
    }
.button {
    background-image: none !important;
    background-color: #101010 !important;
    border: 1px #ff2222 !important;    
    }
span.info {
    color: red !important;
    }
}
