css:
@-moz-document domain("mbd.scout.com") {
.inner-header-menu { width: 100% !important; }
.site-logo { float: left !important; width: 140px; overflow: hidden; margin: 4px 0 0 5px !important; }
.site-logo img { width: 250px !important; height: 30px !important;}
.inner-header-menu > ul { float: right !important; }
.social { display: none !important; }
#header #header-menu { padding: 1px 0 !important; }
#msn-header, #header-top, #header-main, #footer, #footernew, #moderatorbar, #announce, #forumheader .breadcrumb, #announcefooter iframe, #topda728x90, #rotatingad, #TaboolaAd, #p180-root { display: none !important; }
#forumteamnews { padding-bottom:15px !important; }
#promos { min-height:0 !important; height: 0 !important; width:0 !important; }
body { background-color:#fff !important; }
#posttopicform body { margin-top:8px !important; padding: 0 !important; }
#wrapper { position:relative; margin-top:-25px !important; padding:0 0 60px !important; min-width: 500px !important; }
#header.pub #header-menu { position:fixed !important; top:0 !important;  border-bottom:1px solid #555 !important; z-index:1000 !important; width: 100% !important; }
#header.pub #header-menu { background-image: linear-gradient(bottom, #99B4DA 71%, #85A0C7 16%) !important;
background-image: -o-linear-gradient(bottom, #99B4DA 71%, #85A0C7 16%) !important;
background-image: -moz-linear-gradient(bottom, #85A0C7 16%, #99B4DA 71%) !important;
background-image: -webkit-linear-gradient(bottom, #99B4DA 71%, #85A0C7 16%) !important;
background-image: -ms-linear-gradient(bottom, #99B4DA 71%, #85A0C7 16%) !important;
background-image: -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0.71, #99B4DA),
	color-stop(0.16, #85A0C7)
) !important; }
#header.pub #header-menu ul li ul { background-color:#99B4DA !important; top:40px !important; left:-1px !important; border-color: #85A0C7 !important; box-shadow: 0 3px 5px #333; }
#header.pub #header-menu ul li a:hover, #header.pub #header-menu ul li:hover a, #header.pub #header-menu ul li.hover a { background-color:#99B4DA !important; }
#header.pub #header-menu ul li ul a:hover { background-color:#85A0C7 !important; }
#header.pub #header-menu > ul > li { border-right:1px solid #85A0C7; border-left:1px solid #99b4da; }
#forumheader { margin: 0 !important; width:211px !important; min-width:0px !important; position:fixed !important; top:42px !Important; right:95px !important; z-index: 50 !Important; border:none !important; height:26px !important; }
#statusbar { position:fixed !important; top:42px !Important; width:100% !important; height:25px !important; border: none !Important; background:#none !important; background-image: linear-gradient(bottom, #515151 0%, #747474 100%) !important;
background-image: -o-linear-gradient(bottom, #515151 0%, #747474 100%) !important;
background-image: -moz-linear-gradient(bottom, #515151 0%, #747474 100%) !important;
background-image: -webkit-linear-gradient(bottom, #515151 0%, #747474 100%) !important;
background-image: -ms-linear-gradient(bottom, #515151 0%, #747474 100%) !important;
background-image: -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0, #515151),
	color-stop(1, #747474)
) !important; box-shadow: 0 0 5px #333; border-top:1px solid #808080 !important; border-bottom:1px solid #333 !important; z-index:1 !important; margin:0 !Important; padding:0 !important; z-index:1 !important; }
#statusbar .newmail { background:none !Important; }
.inboxandchat { text-align:left !important; }
.inboxandchat span a[title="PM Inbox"] { padding: 0 5px 0 30px !important; display:block !important; color:#fff !important; width:60px !Important; float:right !important; background: transparent url(https://lh5.googleusercontent.com/-aTal0hY0Oqg/UHS5PEHwYfI/AAAAAAAAFLg/dP8B_VLq0lk/s16/mail_mark_unread-1.png) 8px 4px no-repeat !important; line-height:25px !important; position:absolute !important; top:0; right:0; } 
#statusbar .newmail a[title="PM Inbox"] { background-image: url(https://lh4.googleusercontent.com/-85cEFidpy6w/UHS5PFsjskI/AAAAAAAAFLk/JbHjDnZzwiw/s16/mail_mark_unread_new-1.png) !important; }
#statusbar a:hover { text-decoration:none !important; background-color:#747474 !Important; }
#menus { position:fixed !important; top: 42px !important; right: 95px !important; z-index:50 !important; }
#inbox table { width:100% !Important; }
#inbox, #inboxmenu { margin-top:90px !important; }
#first, #last { background: transparent url(https://lh6.googleusercontent.com/-ngI0_53vPfM/UHTb_2MmSfI/AAAAAAAAFMI/AunBzLt-3S0/s16/arrow.png) 3px 5px no-repeat !important; padding: 5px 0 8px 20px !important; }
#menus { border-left:1px solid #515151 !important; border-right:1px solid #747474 !important; margin:0 !important; }
#menus li { width:auto !important; }
#first { width: 105px !important; }
#last { width: 60px !important; }
#menus li > ul { top: 26px !important; border:1px solid #555 !important; box-shadow: 0 3px 5px #333; width:auto !important; padding-bottom:5px !important; border-radius: 0 0 3px 3px !important; background-color:#fff !important; }
#menus >ul > li { border:none !important; border-right:1px solid #515151 !important; border-left:1px solid #747474 !Important; text-align:left !important; }
#left { margin:0 !important; }
#left:nth-child(2) ul li { width: 120px !important; }
#left:hover > div#first, #left:hover > div#last { background-color:#747474 !important; cursor:pointer !important; }
#menus li li { border:none !important; }
#menus ul li ul > li { width: 200px !important; }
#menus ul li ul li a { padding:4px 5px !important; }
#menus ul li ul li a:hover { background-color:#99B4DA !important; color:#fff !important; }
#ChatUrlLink { position:absolute !important; left:0; top:0; }
#bottomcontrol { position:fixed !important; top: 42px !Important; left: 0 !important; width: 500px !important;  height:30px !important; z-index:100 !important; background:none !important; border:none !important; margin:0 !Important; padding:0 !important; }
#bottomcontrol .buttons { width:100% !important; }
#bottomcontrol .buttons a img[title="Post New Topic"] { width:0 !important; height:0 !important; padding-right:120px !Important; padding-bottom:25px !important; background: transparent url(http://lh5.googleusercontent.com/-Ac0OOuDTn6k/UHTs07k16AI/AAAAAAAAFOQ/xClLf_Fq6Rg/h78/new-topic-button.png) left top no-repeat !important; }
#bottomcontrol .buttons a img[title="Add to My Forums"] { width:0 !important; height:0 !important; padding-right:140px !important; padding-bottom:25px !important; background: transparent url(http://lh3.googleusercontent.com/-8htgi006cwM/UHTs07r_-kI/AAAAAAAAFOY/O5E87xbGeDU/h66/add-forum-button.png) left top no-repeat !important; }
#bottomcontrol .buttons a img[title="Reply to Topic"] { width:0 !Important; height:0 !important; padding-right:115px !important; padding-bottom:25px !important; background: transparent url(http://lh6.googleusercontent.com/-zsauH2xTGrs/UHWDyuBqSKI/AAAAAAAAFOw/FdK22up-fns/h81/reply-to-topic.png) top left no-repeat !important; }
.buttons { margin:0 !important; }
.buttons img { border-right:1px solid #515151 !important; border-left: 1px solid #747474 !Important; }
#bottomcontrol .buttons a:hover img { background-color:#747474 !important; }
.topicnav { position:fixed !important; bottom:7px !important; right:7px !important; font-size:80% !important; }
#bottomcontrol .buttons img[title="Quick Reply"] { width:0 !important; height:0 !important; padding-right:100px !important; padding-bottom:25px !important; background: transparent url(http://lh6.googleusercontent.com/-O47QSL9c__U/UHWDymr-2fI/AAAAAAAAFOs/-Q_TrM1Yycw/h93/quick-reply.png) left top no-repeat !important; cursor:pointer !important; }
#bottomcontrol .buttons img[title="Quick Reply"]:hover { background: #747474 url(http://lh6.googleusercontent.com/-O47QSL9c__U/UHWDymr-2fI/AAAAAAAAFOs/-Q_TrM1Yycw/h93/quick-reply.png) left top no-repeat !important; }
#topcontrol .buttons { display:none !Important; }
#topcontrol { height:0 !important; margin:0 !Important; padding:0 !important; }
.pagenumbers { position:fixed !important; bottom: 0px !Important; left: 50% !Important; font-size:80% !Important; height:24px !important; padding-top:2px !important; width:200px !important; }
#onlineusers {position: absolute !important; top: 91px !Important; right: 0 !Important; z-index: 0 !Important; text-align: right !Important; background: none !important; color: white; width:280px !important; }
#onlineusers h4.expand { background:none !important; }
#onlineusers a, #onlineusers h4 { color: #fff !important; }
#onlineusers #OUH a[title="Show/Hide"] { display:none !important; }
#onlineusers:hover p.expand, #onlineusers p.expand:hover {display: block !important; width: 400px !important; text-align: right !important; background:#fff !important; box-shadow: 0 0 8px #333; margin-left:-135px !important; margin-top:2px !important; padding:5px !important; }
#onlineusers p.expand span { float:left !important; }
#onlineusers p.expand span, #onlineusers p.expand a { color:#333 !Important; text-align:left !important;  margin:0 !Important; padding:0 !important; }
#onlineusers p.expand span:after { content:", " !important; padding-right:3px !important; }
#onlineusers p.expand > a { text-align:right !important; clear:left !Important; display:block !important; width: 400px !important; }
#forumstable .categoryrow { padding:0 6px !important; height:27px !important; }
#forumstable { margin-top: 90px !Important; }
.altrow1, .lastrow1, .ratingtd1 td { background-color:#b6cfef !important; }
.altrow2, .lastrow2, .ratingtd2 td { background-color:#efefef !important; }
#announcefooter { position:fixed !important; bottom:0 !Important;; width:100% !important; background-color:#efefef !important; box-shadow: 0 0 5px #555; border-bottom:none !important; border-top:1px solid #333 !important; height:26px !important; margin:0 !important; padding:0 !important; z-index:90 !important; }
#bottomstatus { position:absolute !important; bottom:0px !important; background:none !important; border:none !important; margin:0 !important; padding:0 !important;  }
.bottombcrumb { font-size:80% !important; position:fixed !important; bottom:7px !important; left:0 !important; z-index:150 !important; }
.backtotop { position: absolute !important; bottom:0 !important; right:0 !Important; }
#quickreply { position: fixed; top: 0px !important; opacity: 1 !important; width: 1000px !important; height: 300px !important; }
#quickreply .quickreplybox { position: fixed !Important; top: 63px !Important; left: 240px !Important; width: 500px !Important; border-radius: 0 0 3px 3px !Important; box-shadow: 0 0 10px #000 !important; border:1px solid #333 !important; background-color:#515151 !important; }
.quickreplybox h4 { display:none !Important; }
.quickreplybox img { cursor: pointer; }
#forumteamnews { float:none !important; }
#posttopicform { margin-top:85px !important; float:none !important; padding-top: 10px !important; }
#profilebox { margin-top:90px !important; }
.messagebody img { max-width: 100%; }
}
