@namespace url(http://www.w3.org/1999/xhtml); @-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')  { body { background: url('https://fbcdn-sphotos-e-a.akamaihd.net/hphotos-ak-ash3/561673_364238826992935_1863355866_n.jpg') #000000 no-repeat fixed !important; } #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png'); padding: 20px; -webkit-border-radius: 15px; -moz-border-radius: 15px; border-radius: 15px; } div.l-header .selection_nipple { width: 0; height: 0; border-left: 12px solid transparent; border-right: 12px solid transparent; border-bottom: 12px solid #000; opacity:0.6; font-size: 0; position: relative; top: 4px; vertical-align: top; margin: 0 auto; display: none; } #right_column .controls_section .recessed { background-color: #000; opacity:0.6; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none; -webkit-border-bottom-right-radius: 6px; -webkit-border-bottom-left-radius: 6px; -moz-border-radius-bottomright: 6px; -moz-border-radius-bottomleft: 6px; border-bottom-right-radius: 6px; border-bottom-left-radius: 6px; position: relative; } #right_column .controls_section .recessed:hover { background-color: #000; opacity:0.6; -webkit-box-shadow: 0 0 5px #000; -moz-box-shadow: 0 0 5px #000; box-shadow: 0 0 5px #000; } #right_column .controls_section .selected { background: #c31c16; background: -moz-linear-gradient(top,#c31c16 0,#c31c16 50%,#c31c16 50%,#c31c16 100%); background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#c31c16),color-stop(50%,#c31c16),color-stop(50%,#a61208),color-stop(100%,#a61208)); filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#c31c16',endColorstr='#6B903E',GradientType=0); -webkit-box-shadow: 0 0 5px #c31c16,inset 0 1px 0 rgba(255,255,255,.40); -moz-box-shadow: 0 0 5px #c31c16,inset 0 1px 0 rgba(255,255,255,.40); box-shadow: 0 0 5px #c31c16,inset 0 1px 0 rgba(255,255,255,.40); -webkit-border-radius: 1px; -moz-border-radius: 1px; border-radius: 1px; border-top: none; } #right_column .controls_section .selected:hover { background: #c31c16; background: -moz-linear-gradient(top,#c31c16 0,#c31c16 50%,#c31c16 50%,#c31c16 100%); background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#c31c16),color-stop(50%,#c31c16),color-stop(50%,#a61208),color-stop(100%,#a61208)); filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#c31c16',endColorstr='#6B903E',GradientType=0); -webkit-box-shadow: 0 0 5px #c31c16; -moz-box-shadow: 0 0 5px #c31c16; box-shadow: 0 0 5px #c31c16; border-top: none; } #right_column .radar .content { background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png') repeat-y; position: relative; } #right_column .radar .content.is_media .mask.top { height: 9px; background: none; background-position: 0 0; top: 0; } #right_column .radar .content.is_media .mask.bottom { height: 19px; background: none; background-position: 0 -9px; bottom: 0; } img#content_top, img#content_bottom { display:none; }}
 .post.photo_post .photo, .post.photo_post .video_thumbnail { border: 2px #000 !important; } .content.is_media, .mask.top, .mask.bottom { background: none !important; } /* avatars*/ #left_column #posts .post .post_avatar {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;} #left_column #posts .post .sub_avatar_inner {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;}
