css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.cityofheroes.com") {
/* CoH Forum Custom Style Sheet
 * Version: 0.5
 * Author:  Devian
 * Date:    2009-08-03
 */

/* Override everything to use Verdana */
* {font-family: Verdana !important; }

/* Get rid of the huge background image */
body { background-image: none !important; margin: 5px 0 10px !important; }

/* Get rid of the logo */
body > div > a > img { display: none !important; }

/* Get rid of the horizontal gradients behind the main table, replace with a flat color and thin border */
.page_bgl { border: 1px solid #45494D; background: #191919 !important; }
.page_bgr { background: none !important; padding-top: 15px }

/* Get rid of the vertical gradients in most table cells */
.alt1, .alt1Active, .alt2, .alt2Active {background-image: none !important;}

/* Username in posts in Arial, with a slight shadow and without the underline. */
.bigusername, .bigusername * {text-decoration: none !important; font-family: Arial !important; text-shadow: 0 0 .3em #000 !important; }

/* Black table borders */
.tborder { background: black !important; }
.tborder td {border-color: black !important; }

}
