css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("workflowy.com")
{

/*this will change the site background*/
BODY{
    background-color: #547980 !important;
}
  
/*this will change the color that text goes on*/  
.page {
    background: #E5FCC2 !important;
}

/* Set main font for bullet points and editor */
    BODY, TEXTAREA, DIV.content {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        font-size: 14px !important;
    }   
    
/* set main font for notes and notes editor */
    DIV.noteEditor TEXTAREA,
    DIV.notes DIV.content {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        font-size: 14px !important;
        font-weight: normal;
    }
    
    DIV.notes DIV.content {
        margin: 0 0 0 6px !important;
        padding: 0 0 0 6px !important;
        border-left: 1px solid #D6D6D6 !important;
        width: 100% !important;
    }
    
    DIV.noteEditor TEXTAREA {
        margin: 0 0 0 0 !important;
        padding: 1px 0 0 7px !important;
    }
    
    DIV.nameEditor TEXTAREA {
        margin: 0 0 0 0 !important;
        padding: 1px 0 0 0px !important;
    }
    
/* Show full notes */
    DIV.notes DIV.content {
        height: auto !important;
        overflow: visible !important;
    }
    
/* hide share buttons, get more space, and corners */

div.top.edge{
    display: none;
}

div.bottom.edge{
    display: none;
}

div.left.edge{
    display: none;
}

div.right.edge{
    display: none;
}

DIV.top.right.corner
{
    display: none;
}

DIV.top.left.corner
{
    display: none;
}

DIV.bottom.right.corner
{
    display: none;
}

DIV.bottom.left.corner
{
    display: none;
}

div#share_buttons{
    display: none;
}

div#getMoreSpaceButtonTopLeft
{
    display: none;
    }

/*change the color of the bullets*/

a.bullet{
    color: #547980 !important;
}
    
/* Outer border, set to none */
    #workflowy > DIV.children {
        border-left:none !important;
    }
    
/* border of each child... I've set to none, but you can add this back in */
    DIV.children {
        border-left:none !important;
    }

/* adjust margins */
    #visible {
        margin:10px auto !important;
    }
    
/* Re-style the tags to look more tag-like */
    .contentTag {
        background-color: #0b486b !important;
        border-radius: 3px 3px 3px 3px !important;
        color: #ffffff !important;
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        font-size: 16px !important;
        font-weight: normal !important;
        padding: 1px 1px 1px 1px !important;
        white-space: nowrap !important;
    }

/*Changes the color of tags you are actively browsing*/
    .contentMatch {
        background-color: #0b486b !important;
        }
        
    .contentTagClickable {
        background-color: #0b486b !important;
        }
    
    
/* hide the tags when editing */
    .content.editing .contentTag {
        visibility: hidden;
    }
    
/* remove the underline from tags */
    .content .contentTagText {
        text-decoration: none !important;
    }
}
