css:
/* tokyotosho ahq-style */
@-moz-document domain("tokyotosho.com"),domain("tokyotosho.info") {

body {
font-family: Calibri !important;
font-size: 8pt !important;
color: #ccc !important;
}

a {
text-decoration: none !important;
font-weight: bold !important;
text-transform: uppercase !important;
color: #fff !important;
}
.irc:link {color: #487524 !important; text-decoration: overline !important;}
a:link {color: #949E70 !important;}
a:visited {color: #767A68 !important; font-style: italic !important; text-decoration: none !important;}
a:hover {color: #FFDCA0 !important; text-decoration: blink !important;}
.shade {
background-color: #3B4147 !important;
}
#main {
background-color: #282F35 !important;
}

}
