css:
@-moz-document domain("fark.com"), domain("foobies.com") {
body {   }
 hr { visibility: hidden !important; }
 footer {   }

 #header { height: 30px !important; }
 #logo { height: 0px !important; display: none !important; }
 #headerBottom { margin-top: -105px !important; }
 #headerMenuContainer { height: 25px !important; }
 #headerMenu { font-size: 10pt !important; margin-top: -5px !important; }
 #catMenu { font-size: 10pt !important; margin-top: -15px !important; }
 #rightColSpacer, #rightCol { width: 150px !important; }
 #rightCol>a:nth-child(1) { height: 9px !important; }
 #hotw { margin-top: -10px !important; }
 #newsContainer { background-color: silver !important; }
 #topAd728x90 { display: none !important; height: 0px !important; }
 #tagLine, #userName, #hotw a, .menuSelected a, .catSelected a, .submitLink,  .headlineRow, .headlineRow1 { font-size: 10pt !important; }

 .headline a:visited, .headline a:active { color:purple !important; }
 .mainDate { font-size: 10pt !important; margin-top: -10px !important; }
 .headlineRow td, .headlineRow1 td { padding: 3px 3px !important; }
 .headlineSourceImage { width: 110px !important; }
 .sidebarLinks { border: #000000 1px solid !important; background-color: #696969 !important; padding: 5px !important; margin-top: -10px !important; }
 .sidebarLinks li { width:100% !important; text-align: center !important; font-size: 10pt !important; }

 .ad_container.top, .ad_container.bottom, #abPleaBar, #boxSwap, #footerFarkLinks, #alsoOnFarkTable, #gplus, #facebook, #twitter, #rss, #rightAd300x250, #rightAd300x250Lower, .alsoOnFark,  .shoprotator { display: none !important; }
}
