css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

body{

background: url('http://static.tumblr.com/usldvzy/0n1mbprjn/assets_set_2__3_.jpg') #202020 repeat fixed !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color: rgba(111,96,93,0.5) !important;}

#left_column .dashboard_options_form {
background: url('http://assets.tumblr.com/images/white_20.png') #000000;}

ol#posts li.notification{background-color: #463A38 !important;
border: 0px solid #594C49 !important;
color: #CCC2C1 !important;}
ol#posts li.notification a{color: #86716E !important;text-transform:uppercase;font-size:7pt;text-decoration:none;font-weight:900;}

#right_column a#dashboard_switch_blog_menu_current:hover {background: transparent;}

#right_column .radar .content{background:transparent!important}

#right_column .radar .content .mask{background:transparent!important;}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(240,39,6, 1.0)  !important;}

#nav .nav_item.active{background-color:#403634;}

#left_column #posts .post .permalink{background-color:#2D2524;}

#nav .nav_item.active, #nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{
background-color:#463A38;}

#footer, #footer a {
color:#B4A4A1 !important;
background-image:none !important;}


.post.same_user_as_last:hover, .post.text_post:hover, 
.post.video:hover, .post.audio:hover, .post.photo:hover, 
.post.link:hover, .post.quote:hover, .post.conversation:hover, 
.post.is_reblog:hover, .post.submission:hover, .post.regular:hover, 
.post.question_post:hover, .post.note:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;
}
.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(240,61,11, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
.permalink:hover {background: rgba(95,82,79, 1.0 ) !important;}

.new_post_label{

font-size:0px !important;

}



.new_post_label_icon{background-image:none !important}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://static.tumblr.com/usldvzy/SQOmbprab/assets_set_2__2_.png') center center no-repeat !important;
}






#logo {height: 0 !important;



	width: 0 !important;



	padding-left: 249px !important;

margin-left: -20px !important;
margin-top: 0px !important;
	padding-top: 70px !important;



	background: url(http://static.tumblr.com/usldvzy/yZGmbprko/copy_of_assets_set_2.png) no-repeat !important;}
#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://static.tumblr.com/usldvzy/KOAmbprlc/assets_set_2__1_.png') !important;}


#posts .notification a:not(.avatar_frame) {
color: rgba(147, 127, 123, .56);
font-weight: bold;
text-decoration: none;
border-bottom: 1px solid #382F2D;
-webkit-background-clip: padding-box;}






#posts .notification .nipple.border {
border-top: 7px solid transparent;
border-bottom: 7px solid transparent;
border-right: 7px solid #927F7B;
position: absolute;
left: -7px;
top: 9px;}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 7px solid transparent;
border-bottom: 7px solid transparent;
border-right: 7px solid #927F7B;
position: absolute;
left: -7px;
top: 10px;}

#posts .notification.alt .nipple {
border-right: 7px solid #927F7B;
}

#posts .notification blockquote {
margin: 10px 0 5px 10px;
line-height: 16px;
font-weight: normal;
border-left: solid 2px #A2928F;
padding-left: 15px;
word-wrap: break-word;}
#posts .post .post_info a {
color: #8E7975;
text-decoration: none;
cursor: pointer;}

#posts .post .post_info, #posts .post .post_controls {
font-size: 13px;
font-weight: bold;
color: #8E7975;
min-height: 22px;
line-height: 20px;
vertical-align: middle;
-webkit-font-smoothing: antialiased;
}






.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(130, 110, 105, 1.0) !important;
}


#right_column .controls_section .with_subtitle small {
color: #AD9C98;
font-size: 11px;
font-weight: normal;
}





#pagination a {
color: #CDD2D9;
background-color: rgba(111, 96, 93, 0) !important;
text-decoration: none;
padding: 5px 8px;
margin-left: 10px;
white-space: nowrap;
}






#posts .notification .avatar_frame {
background-color: rgba(111, 96, 93, 0) !important;
border: 1px solid rgba(111, 96, 93, 0) !important;
position: absolute;
top: -1px;
left: -55px;
overflow: hidden;
display: inline-block;
}



#posts .post .post_avatar {
display: block;
width: 64px;
height: 64px;
overflow: hidden;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
border-radius: 6px;
text-decoration: none;
background: rgba(111, 96, 93, .5);
}









}
