css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.auto.ru") {

html {background: #cfcfcf!important;}
body {background: #cfcfcf!important;}

#main-container { background: #cfcfcf!important }
.b-forum-wrapper { background: #cfcfcf!important }
.b-forum-content-wrapper { margin: 0 20px 0 20px!important }
.b-forum-list-part { background-image: none!important}

.topic {
    background: transparent!important;
    padding: 3px 20px !important;
    font-size:13px !important;
}
.topic-name {
    font-size:13px !important;
}
.topic-new-msg {
    background: transparent!important;
    font-size:13px !important;
}

.wwwboard-info {background: transparent!important}
.wwwboards-theme {background: transparent!important}
.wwwboard-topics {margin: 0 20px!important}

.topic.new {background:#efefef!important}
.topic.new:hover {background:#efefef!important}
.topic.new.expand {background:#cfcfcf!important}
.topic:hover {background:#cfcfcf!important}
.post {background: #cfcfcf!important}
.post.new {background:#efefef!important}
.toggle_button { 
         color: #0000EE!important;
         text-decoration:none!important;
}
.topic-author a { 
         text-decoration:none!important;
}
#get-more-topics {background:transparent!important}

.topic .i-topic-content {top:6px!important}
.topic .i-topic-left {top:6px!important}

.posts {margin: 0 0!important}

#top-banner {display: none!important}
#footer {display: none!important}
.b-forum-list-banner {display: none!important}
.avatar {display: none!important}
.topic .fader {display: none!important}
.wwwboards-widget {display: none!important}
.b-forum-widget {display: none!important}
.side-banner.adv-context.adv-1 {display: none!important}
.like.dashed {display: none!important}
.nav {display: none!important}
.sign {display: none!important}

}
