css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('https://www.tumblr.com/login'),
  url('https://www.tumblr.com/'),
  url('http://www.tumblr.com/'),
  url('http://www.tumblr.com/dashboard'),
  url('http://www.tumblr.com/new/blog'),
  url('http://www.tumblr.com/inbox'),
  url('http://www.tumblr.com/help'),
  url('https://www.tumblr.com/preferences'),
  url('http://www.tumblr.com/lookup'),
  url('http://www.tumblr.com/explore'),
  url('http://www.tumblr.com/following'),
  url('http://www.tumblr.com/likes'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/dashboard/'),
  url-prefix('http://www.tumblr.com/new/'),
  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/spotlight/'),
  url-prefix('http://www.tumblr.com/edit/'),
  url-prefix('http://www.tumblr.com/reblog/'),
  url-prefix('http://www.tumblr.com/inbox/after/'), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/inbox/after/") {


/*sub TEXT COLOR begin*/
.post_content,
.new_post_label,
#right_column > UL:first-child > LI:first-child > A > SMALL > DIV,
.post_controls A,
.post_info,
.post_info A,
.post_question_asker,
.footer_links.with_tags.with_source_url A,
.footer_links A,
.footer_links,
.source_url,
.notes,
.notes LI,
.notes_hide_link A,
.read_more_container,
#edit_post .text_field,
.block,
#dashboard_controls A,
#dashboard_controls DIV SPAN,
.dashboard_options_form, .tag_psa,
.dashboard_options_form_disclaimer {
color: rgba(167,167,167, 1) !important;
}

a:link:hover,
A[href^="http"]:hover,
a[href^="/edit"]:hover,
a[href^="/likes"]:hover,
a[href^="/new"]:hover,
a[href^="/tagged"]:hover,
a[href^="/customize"]:hover,
a[href^="/explore"]:hover,
a[href^="/mega-editor"]:hover,
a[id^="ask_answer"]:hover,
a span[id^="note_link"]:hover,
#take_photo_button:hover,
#upload_mp3_tab:hover,
#external_mp3_tab:hover,
.reblog_count span,
.block_link:hover,
#dashboard_controls A:hover,
.following:hover, .add_and_remove:hover, #search_query:hover, #search_controls_button:hover, .settings:hover, .posts:hover, .followers:hover, .messages:hover, .drafts:hover, .queue:hover,
SMALL, .text_field.big.wide, #crushes .count, #l10n_sorry, #footer DIV A:hover,
BUTTON[type="submit"]:hover, .small_links A .hide_overflow:hover, .block:hover, #signup_forms_submit:hover, .sidebar_nav_item A:hover,
#posts .post .post_controls .reblog_count span:hover {
  color: rgba(255,255,255, 0.8) !important;
}

#open_blog_link {
color: rgba(255,255,255,0.6)!important;
}

.post_content a {
color: rgba(255,255,255,0.6)!important;
}
.post .caption a:hover {
color: rgba(255,255,255,0.8)!important;
}

#audio_search_results a{
color: rgba(255,255,255,0.6)!important;
}

#audio_search_results a:hover{
color: rgba(255,255,255,0.9)!important;
}

#audio_search_results {
color: rgba(0,0,0,0.7)!important;
}

#audio_search_results cite {
color: rgba(0,0,0,0.6)!important;
}

.source_url_gradient, #toggle_reasons {
display: none !important;
}


H2,
.optional,
.add_photo_link,
#take_photo_button,
.use_url_link,
#set_avatar_label,
#upload_mp3_tab,
#external_mp3_tab,
.clear_tags,
.advanced_option label,
#slug_url,
.inline_text_field,
.dashboard_options_form_disclaimer a{
color: rgba(255,255,255,0.6)!important;
}
/*sub TEXT COLOR end*/



/*BIG BACKGROUND begin - background for the entire screen*/
body {
  background:url("http://24.media.tumblr.com/tumblr_mbt98wRoPn1qli661o1_500.png") !important; 
  background-repeat: no-repeat !important;
  background-color: rgba(168,172,170, 1) !important;
  background-position: 100% 100% !important;
  background-size: 498px 740px !important;
  background-attachment: fixed !important;
}
/*BIG BACKGROUND end*/


/* HEADER begin*/
div.l-header {
background:rgba(64, 59, 65, 0.50) !important;
border-radius: 15px !important;
border: 2px outset rgba(10,3,0, 0.03);
margin-top: -4px !important;
margin-bottom: 17px !important;
padding-bottom: 6px !important;
box-shadow: 0px 10px 8px -12px #000 !important;
height: 60px !important;}
/* HEADER end*/

/*HOME ICON begin*/
#home_button {
padding-right: 20px !important;
}

.selection_nipple { 
visibility: hidden !important; }

#home_button a {
height: 39px !important;
width: 40px !important;
top: 0px !important;
padding-left: 10px !important;
padding-top: 10px !important;
background: url('http://imageshack.us/a/img14/3168/libbigger.png')no-repeat !important;
opacity: 0.80 !important;
}

#home_button a:hover {
opacity: 1.00 !important;
}
/*HOME ICON end*/


/*ICON BAR begin - new post icons bar - graphic replacement*/
img[src*="http://imageshack.us/a/img90/9988/libiconstestb.png"],
.new_post_label_icon {
opacity: 0.00 !important;  
}

div.l-content #new_post .new_post_label {
font-size: 0px !important;
}

#new_post{
  background-image: url(http://imageshack.us/a/img90/9988/libiconstestb.png) !important;
  background-position: top center !important; 
  background-repeat: no-repeat !important;
  background-color: rgba(58, 56, 57, 1) !important;
}

#posts .post.short_new_post {
display:none;
}

#dashboard_controls {
  height:15px !important;
}
/*ICON BAR  end*/





/*CONTENT begin - background for posts, icon bar, sidebar*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(63,58,64, 0.50) !important;
}
/*CONTENT*/ 



/*POST BACKGROUND begin*/
#posts LI[class*=" post"] {
  background: rgba(64, 59, 65, 1.0) !important;
}
/*POST BACKGROUND end*/



/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/
li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}
/*GREEN BUTTONS end*/


/*RADAR begin*/
div#right_column,
div#right_column:after {
background: none !important;
}
div.radar{
display:none;
}
#recommended_tumblelogs{
display:none;
}
/*RADAR end*/



#content_top, #content_bottom  {
background: transparent !important;
display: none;
}



/*UNSAVED POST begin*/
.flash_notification_wrapper {
background-color: rgba(0,0,0,.55) !important;
color: rgba(255,255,255,0.5) !important;
border-bottom: solid thin rgba(255,255,255,.25) !important;
}

#rescued_post > DIV > A:nth-child(2) {
color:rgba(255,255,255,.25) !important;
}
#rescued_post > DIV > A:nth-child(2):hover {
color:rgba(255,255,255,.6) !important;
}

.answer_container,
#rescued_post > DIV > A:first-child {
background-color: rgba(0,0,0,.15) !important;
color:rgba(255,255,255,.25) !important;
border: solid thin rgba(255,255,255,.15) !important;
border-bottom: solid thin rgba(255,255,255,.15) !important;
}
#rescued_post > DIV > A:first-child:hover {
border-bottom:solid thin rgba(255,255,255,.5) !important;
background-color: rgba(0,0,0,.25) !important;
color: rgba(255,255,255,.7) !important;
}
/*UNSAVED POST end*/



/*POST EDITOR begin - text post message body background*/
.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}

.token {
background-color: rgba(166,165,170,0.7) !important;
border:1px solid rgba(166,165,170,0.8) !important;
}
.token a {
color: rgba(126,126,126,0.8) !important;
}

/*POST EDITOR end*/



/*AVATAR ARROW begin - arrow to the left of the icon bar*/
.arrow{
background:none!important;
width:0px!important;
height:0px!important;
border-right:solid 9px rgba(64, 59, 65, 1.0)!important;
border-bottom:9px solid transparent!important;
border-left: 2px solid transparent!important;
border-top:9px solid transparent!important;
margin-top:0!important;}
/*AVATAR ARROW end*/



/*NOTIFICATION BARS begin*/
div.l-content .notification .nipple {
visibility: hidden !important;
}

div.l-content .notification {
font-family: HelveticaNeue, Helvetica, Arial, sans-serif !important;
background-color: #403e3e !important;
color: rgba(255,255,255,0.6) !important;
line-height: 17px !important;
}

div.l-content .notification.first_notification {
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.05) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
}

div.l-content .notification.alt {
background-color: #4b4a4a !important;
border-left:  rgba(255,255,255,0.05) 1px solid !important;
border-bottom:  rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
}

div.l-content .notification {
border-top:  rgba(45,44,44,0.05) 1px solid !important;
border-left:  rgba(255,255,255,0.08) 1px solid !important;
border-bottom:  rgba(45,44,44,0.4) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
}

div.l-content .notification a {
color: rgba(255,255,255,0.57) !important;
}

div.l-content .notification a:hover {
color: rgba(255,255,255,0.8) !important;
}

.notification .hide_overflow a {
  border-bottom: solid thin rgba(255,255,255,0.3) !important;
}

div.l-content .notification blockquote {
border-color: rgba(255,255,255,0.5) !important;
}


#posts .notification .notification_type_icon {
background-repeat: no-repeat !important;
opacity: 0.6 !important;
}

#posts .notification .notification_type_icon.follow_icon {
opacity: 0.6 !important;
}

#posts .notification .notification_type_icon.like_icon {
opacity: 0.6 !important;
}


#posts .notification .notification_type_icon.answer_icon {
opacity: 0.6 !important;
}

#posts .notification .notification_type_icon.reblog_icon {
opacity: 0.6 !important;
}

#posts .notification .notification_type_icon:hover,
#posts .notification .notification_type_icon.follow_icon:hover,
#posts .notification .notification_type_icon.like_icon:hover,
#posts .notification .notification_type_icon.answer_icon:hover,
#posts .notification .notification_type_icon.reblog_icon:hover {
background-repeat: no-repeat !important;
opacity: 0.9 !important;
}
/*NOTIFICATION BARS end*/

/* AVATARS begin*/

.avatar_frame, .post_avatar, .avatar {
background-color: transparent !important;
border-color: transparent !important;
}

div.l-content #posts .notification .avatar_frame {
border: 0px solid !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.26);
}
/* AVATARS end*/


/*RETURN TO TOP begin*/
.return_to_top_icon {
  content: "" !important;
  color: blue !important;
  background: rgba(168,172,170, 1) !important;
  -moz-border-radius: 8px !important;
  -webkit-border-radius: 8px !important;
  border-radius: 8px !important;
}
.return_to_top_icon:hover {
  background: rgba(96,96,96,0.1) !important;
}
.return_to_top_icon:before {
  width: 0px !important;
  position: absolute !important;
  content:"" !important;
  border-right: solid 13px transparent !important;
  border-bottom: solid 13px rgba(0,0,0,.75) !important;
  border-left: solid 13px transparent !important;
  margin-top: 12px !important;
  margin-left: 12px !important;
}
.return_to_top_icon:after {
  position: absolute !important;
  content:"" !important;
  width: 12px !important;
  height: 13px !important;
  background-color: rgba(0,0,0,.75) !important;
  margin-top: 25px !important;
  margin-left: 19px !important;
}
/*RETURN TO TOP end*/



/*TIME - DATE begin*/
a[class*="permalink"] {
  background: none !important;
}
.permalink:before {
  content: attr(title) !important;
  color: rgba(255,255,255, 0.50) !important;
  position: absolute !important;
  display: block !important;
  width: 300px  !important;
  margin-left: -300px !important;
  margin-top: 4px !important;  
  text-align: right !important;
  font: bold .8em helvetica !important;
  text-transform: lowercase !important;
}
.permalink:hover::before {
  color: rgba(255,255,255, 0.80) !important;
}
/*TIME - DATE end*/



/*LOADING MORE POSTS begin*/
#auto_pagination_loader {
  -moz-box-shadow: rgba(0,0,0,1) 0 1px 0 !important;
  -webkit-box-shadow: rgba(0,0,0,1) 0 1px 0 !important;
  box-shadow: rgba(0,0,0,1) 0 1px 0 !important;
  padding: 9px 8px !important;
  text-align: center !important;
  border-top: 1px solid rgba(192,192,192, 0.50) !important;
  background: -moz-linear-gradient(bottom, rgba(0,0,0, 0.25), rgba(134,133,133, 0.25)) !important;
  background: -webkit-gradient(linear, 0% 100%, 0% 0%, from(rgba(0,0,0,0.25)), to(rgba(255,255,255, 0.25))) !important;
  font-size: 1.00em !important;
  text-shadow: rgba(0,0,0,.6) 0 1px 0 !important;
  color: rgba(255, 255, 255, 1.00) !important;
}
/*LOADING MORE POSTS end*/



/* NOTES */
div.l-content #posts .notes .note {
background: rgba(0,0,0,0.13) !important;
color: rgba(255,255,255,0.44) !important;
}

div.l-content #posts .notes .note .avatar {
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
}

div.l-content #posts .notes .note a {
color: rgba(255,255,255,0.5) !important;
}

div.l-content #posts .notes .note a:hover {
color: rgba(255,255,255,0.8) !important;
}

div.l-content #posts .notes blockquote {
border-color: rgba(255,255,255,0.44) !important;
}

div.l-content #posts .notes_control a {
color: rgba(255,255,255,0.6) !important;
}

div.l-content #posts .notes_control a:hover {
color: rgba(255,255,255,0.9) !important;
}

/*NOTES end*/


/*POST BAR FIX begin*/
#content_top.content_cap,
#content_bottom.content_cap {
  display: none !important;
}
#dashboard_edit_post #container div.l-content #edit_post_controls,
#dashboard_edit_post #container div.l-content #left_column > H1 {
  margin-top: 25px !important;
} 
#dashboard_edit_post #container #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  border-radius: 14px !important;
}
/*POST BAR FIX end*/


/*CHAT LINES color alteration*/
div.l-content .conversation_lines {
border: 1px solid rgba(0,0,0,0.08) !important;
border-radius: 4px !important;
}

div.l-content .conversation_lines .chat_line {
background-color: rgba(0,0,0,0.25) !important;
border-color: rgba(0,0,0,0.08) !important;
color: rgba(255,255,255,0.5) !important;
}
/*CHAT LINES color alteration end*/

/* TAB NOTICE */
#new_post_notice_container.tab_notice {
color: #ffffff !important;
background-color: rgba(100,99,98,0.7) !important;
}

#dashboard span.tab_notice_value {
color: #000000 !important;
}

#container .selection_nipple, #container .tab_notice_nipple {
visibility: hidden;
}
/* TAB NOTICE */

#new_post_notice_container,.tab_notice {
color: #ffffff !important;
background-color: rgba(100,99,98,0.7) !important;
}

#dashboard span.tab_notice_value {
color: #000000 !important;
}

/*FANMAIL*/
.fan_mail, .message {
background: none !important;
background-color: rgba(64, 59, 65, 1.0) !important;
color: rgba(167,167,167, 1) !important;
font-family: sans-serif, helvetica, tahoma, arial !important;
font-size: 14px !important;
line-height: 20px !important;
}

.message a {
color: rgba(167,167,167, 1) !important;
}

#right_column a.send_fan_mail {
background-image: none !important;
text-shadow: none !important;
box-shadow: none !important;
border-color:rgba(0,0,0, 0.2)!important;
border-radius: 8px !important;
-webkit-border-radius: 6px !important;
}

#dashboard_inbox #posts div, #dashboard_submissions #posts div {
background-image: none !important;
}

/*USER MENU start*/
.user_menu_list a {
background:rgba(64,59,65, 1) !important;
color:rgba(167,167,167, 1) !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.05) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
border-radius:0px !important;
}
/*USER MENU end*/

/*PAGE LINK*/
#next_page_link {
background: rgba(64, 59, 65, 0.6) !important;
border-bottom:2px solid rgba(155,157,149,0.4) !important;
color: rgba(167,167,167, 1) !important;
opacity: 0.8 !important;
border: 2px solid rgba(155,157,149,0.4);
border-radius: 15px !important;
-webkit-border-radius: 6px !important;
transition: all 0.5s ease;
}

#next_page_link:hover{
color: rgba(255,255,255, 0.9) !important;
border: 2px solid rgba(155,157,149,0.7);
background: rgba(64, 59, 65, 0.8) !important;
transition: all 0.5s ease;
}

#next_page_link:active {
color: rgba(255,255,255, 1) !important;
background: rgba(0, 0, 0, 0.6) !important;
transition: all 0.3s ease;
}

#previous_page_link{
background: rgba(64, 59, 65, 0.6) !important;
border-bottom:2px solid rgba(155,157,149,0.4) !important;
color: rgba(167,167,167, 1) !important;
opacity: 0.8 !important;
border: 2px solid rgba(155,157,149,0.4);
border-radius: 15px !important;
-webkit-border-radius: 6px !important;
transition: all 0.5s ease;
}

#previous_page_link:hover{
color: rgba(255,255,255, 0.9) !important;
border: 2px solid rgba(155,157,149,0.7);
background: rgba(64, 59, 65, 0.9) !important;
transition: all 0.5s ease;
}

#previous_page_link:active {
color: rgba(255,255,255, 1) !important;
background: rgba(0, 0, 0, 0.6) !important;
transition: all 0.3s ease;
}

/*QUEUE COLOUR begin*/
.dashboard_options_form {
background:rgba(68,68,68,1) !important;
border-top:rgba(0,0,0,1) !important;
border-bottom:rgba(0,0,0,1) !important;
border-left:rgba(0,0,0,1) !important;
border-right:rgba(0,0,0,1) !important;
}
/*QUEUE COLOUR end*/


/*FOLLOWERS PAGE begin*/
.white_pane {
background:rgba(64,59,65,0.5) !important;
border-style:solid !important;
border-color:rgba(255,255,255,0.4) !important;
}

.name a,.description a {
color:rgba(255,255,255,0.55) !important;
 }

.title_and_controls h1 {
color:rgba(255,255,255,0.55) !important;
}
.white_pane .follower{
background:rgba(68,68,68,0.5) !important;
}

.white_pane .follower.alt {
background:rgba(68,68,68,1) !important;
}

div.l-content :-moz-placeholder {
background:rgba(68,68,68,0.2) !important;
color:rgba(255,255,255,0.35) !important;
}

#user_to_check_for {
background:rgba(68,68,68,0.2) !important;
color:rgba(255,255,255,0.75) !important;
}

.white_pane .pagination a {
background: rgba(64, 59, 65, 0.6) !important;
border-bottom:2px solid rgba(155,157,149,0.4) !important;
color: rgba(167,167,167, 1) !important;
opacity: 0.8 !important;
border: 2px solid rgba(155,157,149,0.4);
border-radius: 15px !important;
-webkit-border-radius: 6px !important;
transition: all 0.5s ease;
}

.white_pane .pagination a:hover{
color: rgba(255,255,255, 0.9) !important;
border: 2px solid rgba(155,157,149,0.7);
background: rgba(64, 59, 65, 0.8) !important;
transition: all 0.5s ease;
}

.white_pane .pagination a:active {
color: rgba(255,255,255, 1) !important;
background: rgba(0, 0, 0, 0.6) !important;
transition: all 0.3s ease;
}
/*FOLLOWERS PAGE end*/


/*MISC begin*/
.no_posts_found{
color: rgba(255,255,255,0.4) !important;
text-align: center;
padding-top:50px !important;
}

.photo_reply_icon_mask {
  background-size: 20px 17px !important;
  background-color: rgba(64, 59, 65, 1) !important;
  opacity: 0 !important;
  background-image: url("data:image/png;base64,") !important;}
.post_control.photo_reply_icon.active:hover .photo_reply_icon_mask {
  background-color: rgba(64, 59, 65, 1) !important;
  background-size: 20px 17px !important;
  opacity: 0 !important;
  background-image: url("data:image/png;base64,")
}

#MissingE_safeDash {
display:none !important;
}

#posts .post .footer_links .tags {
line-height: 12px !important;
}
.source_url {
height: 12px !important;
line-height: 12px !important;
}

.MissingE_notesSorter {
color:rgba(255,255,255,0.5) !important;
}
.MissingE_sorterContainer div,.MissingE_unsort {
color:rgba(0,0,0,1) !important;
background:rgba(255,255,255,0.4) !important;
border:transparent !important;
} 
/*MISC end*/



/*MOVE MAIN BODY left or right begin*/
#logged_in #container,
#logged_in #footer {
  position: relative !important;
  float:left !important;
}
/*MOVE MAIN BODY end*/

/*DASH CONTROLS IMAGE begin*/
#dashboard_controls_open_blog {
  border-color: rgba(100,99,98,0.5) !important;
}
#dashboard_controls_open_blog:hover {
  border-color: #646362 !important;
}
#dashboard_controls_open_blog:before {
  opacity: 1  !important;
   -moz-transition: ease-out all .15s !important;
   -webkit-transition: ease-in all .15s !important;
}
#dashboard_controls_open_blog:hover:before {
  z-index: 0 !important;
  opacity: 0 !important;
    -moz-transition: ease-in all .25s !important;
    -webkit-transition: ease-in all .25s !important;
}

#dashboard_controls_open_blog:before {
  position: absolute !important;
  content: "" !important;
  width: 215px !important;
  height: 170px !important;
  z-index: 1 !important;
  background-color: #646362 !important;
  background-image: url("http://imageshack.us/a/img842/433/blakes7logo.jpg") !important;
  background-size: 215px 170px !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  border: solid thin rgba(0,0,0,0) !important;
  border-radius: 8px !important;
}
/*DASH CONTROLS IMAGE begin*/

/*TAG COLOUR FIX*/
#popover_tracked_tags > DIV {
  background: rgba(116,115,117,1) !important;
}
#popover_tracked_tags > DIV,
#popover_tracked_tags > DIV > DIV > DIV,
.popover_menu_item.ender  {
  border-color: rgba(62,57,62,0.4) !important;
  box-shadow: none !important;
}
.popover_menu_item.tracked_tag a .hide_overflow,
.popover_menu_item.ender a,
a[id^="tag"] small[class="count"] {
  color: rgba(255,255,255,.65) !important;
}
.popover_menu_item.tracked_tag a .hide_overflow:hover,
.popover_menu_item.ender a:hover,
a[id^="tag"] small[class="count"]:hover {
  color: rgba(255,255,255,1) !important;
}
/*TAG COLOUR FIX end*/

/*SEARCH COLOUR FIX begin*/
#search_query.search_field_active{
background: rgba(0,0,0,0.16) !important;
color: rgba(255,255,255,0.55) !important;
}
/*SEARCH COLOUR FIX end*/

/* FOOTER */
#footer, #footer a {
color: rgba(0,0,0,0.4) !important;
border: none !important;
text-align:right !important;
}
/*FOOTER end*/

