css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("troubletickets.smokinjoe.com") {


body {
background-image:url('http://i.imgur.com/erZFE.jpg') !important;
background-repeat:repeat !important;
background-attachment:fixed !important;
background-position:center !important;
}

table{
width: 950px;
margin:auto;
background-color: transparent !important;
}

td{
opacity:0.7;
text-align:center;
}

.style4{
opacity:0.7;
}

#form1 td{
background-color:#666666;
border-style:solid;
border-width:2px;
border-color:black;
text-align:left;
}

#form1 td div{
padding:5px;
opacity:1;
}

#form1 input {
		font-size:12px;
		color:#000;
		text-decoration:none;
		display:block;
		width:100px;
		padding:5px;
		border:1px solid #DDD;
		text-align:center;

		border-radius:5px;
		-webkit-border-radius:5px;
		-o-border-radius:5px;
		border-radius:5px;
	}
#form1 input:hover {
		color:#009;
		border-color:#3278BE;
	
}





  


}
