css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("u-cursos.cl") {
div#izquierda {text-decoration: none !important;}
div#izquierda div h1 {background-color: #ddf0dd !important;border: 1px solid #aaccaa !important;text-decoration: none !important;}
div#izquierda div h1 a {color: #006666 !important;text-decoration: none !important;font-weight: bold !important;}
div#izquierda div ul li {text-decoration: none !important;border-right: 1px solid rgb(239, 239, 239) !important;border-style: none solid solid;border-color: rgb(239, 239, 239) rgb(239, 239, 239) !important;background-color: white !important;}
div#izquierda div ul li:hover {background-color: #DBE7F5 !important;text-decoration: none !important;}
div#izquierda div ul li a {color: #4A4F88 !important;text-decoration: none !important;}
div#izquierda div ul li a:hover {background-color: #DBE7F5 !important;text-decoration: none !important;}
div#izquierda div ul li.sel {font-weight: bold !important; background-color: #F1F4F8 !important;}
div#izquierda div ul li.sel a {font-weight: bold !important;}
div#izquierda div.off h1 a {color: #BC0000 !important;text-decoration: none !important;}
h1#ucursos {background: url("http://imageshack.us/a/img23/9651/scuolastyle.png") no-repeat scroll left top rgb(232, 232, 232) !important;border-bottom: 5px solid rgb(80, 144, 80) !important;}
h2.ucursos {background-image: url("https://www.u-cursos.cl/scuola/diseno/blanco.png") !important;background-position: right top !important;border-bottom: 5px solid rgb(80, 144, 80) !important;color: rgb(28, 29, 176) !important;}
div.instrucciones{padding: 15px;background: transparent url( 'https://www.u-cursos.cl/diseno/images/gris.png' ) scroll repeat top left !important;border: 1px solid #ccc !important;max-width: 500px;}
h1.modulo {color: #1C1DB0 !important;border-bottom: solid 1px #1C1DB0 !important;}
.boton {color: #404668 !important;height: 20px;line-height: 100% !important;}
.boton:hover{color: #0066cc !important;}
#body a {text-decoration: underline;}
a {color: #509050 !important;text-decoration: none !important;}
a:visited {color: #0066cc !important; text-decoration: none !important;}
a:hover{color: #0066cc !important; text-decoration: underline !important;}
a:active {color: #007979 !important;text-decoration: underline !important;font-weight: normal;}
a:link {color: #0066cc !important; text-decoration: none !important;}
h1{}
h2 {text-decoration: none !important;}
div.instrucciones *{text-align: justify !important;}
div.nuevo span.autor a, div.connuevo h1 a {color: #007979 !important;text-decoration: underline !important;font-weight: bold !important;}
div.msg {text-decoration: none !important;}
div.msg h1 a {color: #007979 !important;font-weight: normal;font-size: 14px; }
div.msg ul.metadata li a {font-weight: normal !important;color: #933633 !important;text-decoration: underline !important}
span.autor a {color: #007979 !important;text-decoration: underline !important;font-weight: normal;}
ul.modulo li.sel a {background: #1C1DB0 !important;color: white !important;border-top: 0px solid #5C6C8E !important;}
ul#servicios_generales li a{color: #1C1DB0 !important;}
table, th, td {border: 1px solid black !important;}
table{background: #fff !important;}
th {background: #D9ECA1 !important;color: #000080 !important;}
tr.separador{background-color: #ECF5D0 !important;color: #5C6C8E !important;}
td.verde, td.opciones{background: #f9f4e8 !important;}
td.gris{background: #f4f4f4 !important;}
ol, ul {text-decoration: none !important;}
ul.modulos a {text-decoration: none !important;}
ul.modulo li{border-right: 1px solid #1C1DB0 !important;text-decoration: none !important;}
ul.modulo li a {color: #959595 !important;text-decoration: none !important;}
ul.modulos li a:hover span {text-decoration: none !important;}
ul.modulos li span {text-decoration: none !important;}
ul.modulos li.sel, ul.modulos li.sel:hover {border-color: #1C1DB0 !important;text-decoration: none !important;}
ul.opciones li a {font-weight: normal;color: #1B1EB0 !important;text-decoration: underline !important;}
ul#navbar {text-decoration: none !important;}
}
