css:
@-moz-document url("http://www.neopets.com/"), url-prefix("http://www.neopets.com/neoboards"), url-prefix("http://www.neopets.com/pirates"), url-prefix("http://www.neopets.com/customise/"), url-prefix("http://www.neopets.com/games/"), url-prefix("http://www.neopets.com/nf"), url-prefix("http://www.neopets.com/island/"), url-prefix("http://www.neopets.com/faerieland/"), url-prefix("http://www.neopets.com/objects"), url-prefix("http://www.neopets.com/market"), url-prefix("http://www.neopets.com/stockmarket"), url-prefix("http://www.neopets.com/challenges/"), url-prefix("http://www.neopets.com/halloween/"), url-prefix("http://www.neopets.com/index.phtml"), url-prefix("http://www.neopets.com/water/"), url-prefix("http://www.neopets.com/desert/"), url-prefix("http://www.neopets.com/quickref"), url-prefix("http://www.neopets.com/edit"), url-prefix("http://www.neopets.com/habitarium"), regexp("www\\.neopets\\.com/(?!guilds/guild\\.phtml.*|browseshop\\.phtml.*|userlookup\\.phtml?.*|~.*)") {
body.en {background:url(http://img42.imageshack.us/img42/6009/starrybardark1.png) top left repeat-y, url(http://i3.photobucket.com/albums/y60/eitsuop/noisebg.png) repeat !important; font-family: tahoma;}
.brand-mamabar, .activePet.sf, .sidebarHeader:first-of-type, #ban,#footer, #ad-slug-wrapper, .ad_wrapper_fixed, .ad_wrapper, .neopetPhrase, form[action="process_topic.phtml"] .contentModuleHeaderAlt,  form[action="process_topic.phtml"] td[width="150"], #boards_table > tbody > tr:first-child {display:none;}

.content a:hover, .user a b:hover {color:#3A86C8!important;}
.content a:link,.content a:visited, .user a b {color:#235580;}

#header {width:100%!important;height:33px!important;position:fixed!important;top:0!important;left:0!important;background-image:none!important;background-color:#B53434!important;z-index:99!important;border-bottom:2px solid #6B1F1F;}

#navigation img { display:none;height:33px; visibility: hidden;}

#template_nav img {display: block; padding:0!important;}
#template_nav .nav_image {position:absolute;}
#template_nav li:nth-of-type(1) a:before {content:"my account";}
#template_nav li:nth-of-type(2) a:before {content:"customise"}
#template_nav li:nth-of-type(3) a:before {content:"games"}
#template_nav li:nth-of-type(4) a:before {content:"explore"}
#template_nav li:nth-of-type(5) a:before {content:"news"}
#template_nav li:nth-of-type(6) a:before {content:"boards"}
#template_nav li:nth-of-type(7) a:before {content:"shops"}
#template_nav li:nth-of-type(8) a:before {content:"nc mall"}
#template_nav li:nth-of-type(9) a:before {content:"premium"}
#template_nav li:nth-of-type(9) {right:0!important;}
#template_nav li:nth-of-type(8) {right:10%!important;}
#template_nav li:nth-of-type(7) {right:20%!important;}
#template_nav li:nth-of-type(6) {right:30%!important;}
#template_nav li:nth-of-type(5) {right:40%!important;}
#template_nav li:nth-of-type(4) {right:50%!important;}
#template_nav li:nth-of-type(3) {right:60%!important;}
#template_nav li:nth-of-type(2) {right:70%!important;}
#template_nav li:nth-of-type(1) {right:80%!important;}
#header a[href="/index.phtml"] {height:20px;z-index:0!important;position:absolute;top:0;left:0;height:0px;width:10%;}
#header a[href="/index.phtml"]:before {content:"home"}
.nav_image a:first-of-type, #header a[href="/index.phtml"] {color:#FFF!important;display:block;height:10px;padding:5px 0;text-align:center;z-index:4;}
.nav_image {border-bottom:0px solid #3EA0D9;-webkit-transition:0.2;}

.nav_image {width:10%!important;background-color:#B53434!important;display:block;float:right;}
#template_nav .dropdown a:before {content:""!important;}

ul.dropdown {border: 0px!important;padding: 5px!important; margin: 2px 4px!important; border:0px!important;background:none!important;position:relative;left:0px;top:0;text-align:left!important;}
.dropdown li a {display:block!important;border:0!important;font-weight:normal;color:#FFF!important;font-size:10px;line-height:12px;padding:0px 1px 2px 1px;text-align:left!important;}
.dropdown li a:hover {background-color:#FF9F9F;}

.user {position:absolute;top:0;right:0;}
#navigation {width:100%!important;position:absolute;right:0;top:0;}
#navigation td {width:100%!important;float:right;}

.sidebar, .sidebar * {z-index:98!important;background:0!important;color:#DDD!important;font-weight:100;}
.sidebar img {width:100px; height:100px;}
.sidebar {position:fixed;top:28px;left:0;height:100%;width:100px;}
.sidebarTable {width:100px!important;}
.sidebarModule,.activePet, .neofriend * {padding:0!important;margin:0!important;}
.sidebarModule, .sidebarTable, .sidebarHeader, .activePet, #main, .neofriend * {border:0!important;}
.neofriendUsername {background:#282828!important;padding-right:5px;width:94px;float:left;}
.neofriend {visibility:hidden;width:0px!important;}
.neofriend table {visibility:visible;color:#DDD!important;}
.neofriend img {display:none;width:0px;height:0px;}
.activePetInfo * {color:#222223;font:7px verdana;}
.activePetInfo {background-color:#222223!important;text-align:left;width:100%;font:7px verdana; margin-bottom:-20px;}

#main {width:100%!important;height:100%!important;position:absolute;top:0;border-top:36px solid rgba(0,0,0,0)!important;border-left:105px solid rgba(0,0,0,0)!important;overflow:auto;background:none!important;padding:0!important;margin:0!important;
-moz-box-sizing:    border-box;
    -webkit-box-sizing: border-box;
    box-sizing:        border-box;
padding-top:18px!important;
padding-bottom:30px;
}

.user {position:fixed;top:40px;right:115px;z-index:96!important;color:#FFF!important;height:20px;}

.contentModuleHeaderAlt {background:#53B3EB!important;}

.dropdown li{margin-left:-16px;}
.dropdown{overflow:hidden;} 

.eventIcon {position:fixed!important;top:35px!important;left:100px!important;width:100%!important;padding:10px!important;background:rgba(0,0,0,0.7)!important;color:#FFF!important;z-index:1!important;height:5px!important;}

.marqueeSlot {background-color:#53B3EB!important;}
.marqueeSlotOn {background-color:#3EA0D9!important;}
.marqueeSlot a, .marqueeSlotOn a {color:#FFF!important;background:0!important;}

#nst {position:fixed;top:40px;right:65px;margin-right:-60px;color:#FFF!important;z-index:150!important;max-width:120px!important;font-size:8pt!important;text-align:right;}

::-webkit-scrollbar {
    width: 12px;
}
 
::-webkit-scrollbar-track {
background:rgba(0,0,0,0.1);
}

#main::-webkit-scrollbar-track {
margin-top:44px;
}

::-webkit-scrollbar-thumb {
background:rgba(0,0,0,0.2);
-webkit-transition:background 0.2s;
}
::-webkit-scrollbar-thumb:hover {
background:rgba(0,0,0,0.3);
}

.eventIcon > b {display:none;}

.sidebarModule {opacity:0!important;}
.sidebarModule:first-child, .sidebarModule:last-child {opacity:1!important;}
.sidebarModule:last-child {position:absolute;top:180px;}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/") {
.content > div > table, #boards_table {background:#FFF;}
form[action="process_topic.phtml"] table {
border:0!important;width:628px!important;background:none!important;}
form[action="process_topic.phtml"] td {padding:0!important;}
form[action="process_topic.phtml"] textarea {width:624px!important;}
form[action="process_topic.phtml"] table table td {width:62px!important;padding:10px 0!important;}

#boards_table > tbody > tr:last-child {background:#FFF!important;}
}

@-moz-document url("http://www.neopets.com/neoboards/create_topic.phtml ") {
form > table:first-of-type input {width:728px!important;margin:0!important; float:center;}

form > table:nth-of-type(2) {width:728px!important;}

form > table table td[valign="top"]:nth-of-type(2) {;}
}

@-moz-document url-prefix("http://www.neopets.com/ ") {
#content, .content {
	width:100%; 
}
#content {padding-bottom:60px;}
body{
	font-size:10pt;
	
}
.boards_table, #boards_table {
	width:100% !important;
}

#brand-mamabar,#brand-mamabar-wrapper,#ban {display:none;height:0px;}
}

@-moz-document url-prefix("http://www.neopets.com/guilds/guild_board.phtml?") {
td.content > table {width:800px;}
td.content .content table {width:800px; float:center;}

form > table table td {padding:10px;float:center;}

#content, .content {
	width:100%; 
}
#content {padding-bottom:60px;}
body{
	font-size:10pt;
	
}

#main {width:100%!important;height:100%!important;position:absolute;top:0;border-top:36px solid rgba(0,0,0,0)!important;border-left:105px solid rgba(0,0,0,0)!important;overflow:auto;background:none!important;padding:0!important;margin:0!important;
-moz-box-sizing:    border-box;
    -webkit-box-sizing: border-box;
    box-sizing:        border-box;
}
}
