css:
/* about.com avatar fix version 0.1.1 */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.about.com") {

td.pfMsgBio {
height:150px !important;
}

div.pfMessageDisplayPostcard td.ptcListContainerCellRight {
position:relative !important;
float:right !important;
top:-30px !important;
height:8px !important;
}


td.ptcListContainerCellLeft {
width:90px !important; 
}

ul.ptcListStack {
width:210px !important;
}

td.pfMsgEnvelope td.ptcListContainerCellLeft ul.ptcListStack {
position:relative !important;
left:+310px !important;
top:-160px !important;
}

td.pfMsgEnvelope td.ptcListContainerCell ul.ptcListStack {
position:relative !important;
left:+98px !important;
top:-105px !important;
}

div#PTID-messageList-messageDisplay-body {
position:relative !important;
top:-60px !important;
}

span.ptbReply {
position:relative !important;
top:-75px !important;
}

span.ptbEdit {
position:relative !important;
top:-40px !important;
left:-100px !important;
}

span.ptbDelete {
position:relative !important;
top:-40px !important;
left:-100px !important;
}

span.ptbSetPrivs {
position:relative !important;
top:-40px !important;
}

td.pfMsgActionBar span.ptcDelimiter {
position:relative !important;
top:-40px !important;
}

span.ptbReportViolation {
position:relative !important;
top:-40px !important;
}

}
