css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pandora.com") {
#brandingBar {
height: 0px !important;
}

#brandingBar .leftcolumn{
display: none !important;
}

#playerBar .leftcolumn{
width: 0px !important;
margin: 0px !important;
}

#brandingBar .stationChangeSelectorNoMenu{
display: none !important;
}

.searchBox{
position: absolute !important;
margin-top: 55px !important;
left: -228px !important;
}

#playerBar .buttons{
margin: 0px !important;
}

#playerBar .progress{
margin: 0px !important;
}

#brandingBar .notAnonymousUser{
margin-top: 65px !important;
margin-right: 10px !important;
}

#brandingBar .anonymousUser{
margin-top: 65px !important;
margin-right: 10px !important;
}

#brandingBar .registeredUser{
margin-top: 65px !important;
}

#playerBar .rightcolumn{
float:left !important;
width: 227px !important;
}

#playerBar #playbackControl{
overflow:visible !important;
float:left !important;
display:block !important;
}

#playerBar .info{
width:166px !important;
}

#playerBar .progress{
position: relative !important;
top: 3px !important;
left: -124px !important;
}

.disabled{
display: none !important;
}
}
