css:
@-moz-document domain("kinopoisk.ru") {

#block_left_index .block_left_index {
    width: 100%;
}

#brandingFlash, #top_superbanner, #hide_adv, #top_3banners {
    display: none ;
}

#content_block {
    margin-top: 24px ;
}

#content_block .movie table.info td:first-child {
    font-size: 13px ;
    width: 150px ;
}

#content_block .movie table.info td:last-child {
    font-size: 13px ;
    width: 420px ;
}

#content_block, #content_block .movie > table {
    width: 1118px ;
}

#loadb, #yandex_placement {
    display: none ;
}

#promo_player_container {
    display: none ;
    z-index: -1 ;
}

#top {
    height: 70px;
    margin-top: -20px;
    width: 100%;
}

#top #spec_trailer, #top .branding_flash {
    display: none ;
    height: 0 ;
    width: 0 ;
    z-index: -1 ;
}

#top .master, #top_form {
    top: 20px ;
}

#top .menu, #top #top_form, #top .png_block {
    top: 18px ;
}

#top .png_block {
    width: 100%;
}

#top_form .logo {
    left: 200px;
}

#top_form .search_line_image {
    display: none ;
}

#top_form div div {
    display: none;
}

.block_left_padtop {
    width: 771px ;
}

.buyTicketFilm {
    left: 790px ;
}

.film_menu {
    border-top: 1px dotted #BBB ;
    font-size: 12px ;
    height: 28px ;
}

.film_menu li {
    border-top: 1px dotted #E0E0E0 ;
    height: 20px ;
    padding-left: 12px ;
    padding-right: 12px ;
    padding-top: 6px ;
}

.film_menu li s {
    background: none ;
}

.film_menu li u {
    background: none ;
    border-right: 1px solid #BBB ;
    height: 26px ;
}

.film_menu li:first-child {
    -webkit-border-bottom-left-radius: 4px ;
    border-bottom-left-radius: 4px ;
    border-left: 1px solid #BBB ;
}

.film_menu li:hover {
    background-color: #FEFEFE ;
}

.film_menu li:last-child {
    -webkit-border-bottom-right-radius: 4px ;
    border-bottom-right-radius: 4px ;
    border-right: 1px solid #BBB ;
}

.film_menu li:last-child u {
    border: none ;
}

.main_line {
    font-size: 13px ;
}

.response .brand_words {
    width: 709px ;
}

.shadow {
    background-color: #FAFAFA ;
    margin: 0 auto ;
    width: 1110px ;
}

.shadow_top {
    background-color: #f60;
    background-image: none ;
    height: 9px ;
    margin: 0 auto ;
    position: static ;
    width: 1100px ;
}

.triviaBlock {
    font-size: 14px ;
    line-height: 136% ;
}

.triviaBlock li {
    padding-bottom: 6px ;
}

.userReview {
    width: 739px ;
}

body {
    background-image: url(data:image/pjpeg;base64,) ;
    background-repeat: repeat ;
    width: 99.8% ;
}

div.response p {
    font-size: 14px ;
    line-height: 136% ;
}

div.response p.sub_title {
    width: 537px ;
}

form.response input.text, form.response textarea {
    background-color: #FFF ;
    font-size: 14px ;
    padding-bottom: 5px ;
    padding-left: 8px ;
    padding-right: 8px ;
    padding-top: 5px ;
    width: 700px ;
}

form.response input.text:focus, form.response textarea:focus {
    background-color: #FFC ;
}

h1.moviename-big {
    font-size: 30px ;
}

img.logo {
    display: none;
}

input.searchButton1 {
    display: none;
}

input.searchButton2 {
    display: none;
}

table[style*="width: 850px"] {
    width: 100% ;
}

td.news {
    font-size: 14px ;
}

td.news .brand_words {
    font-size: 16px ;
    line-height: 136% ;
}

}
