url-prefix('http://krautchan.net/d'),url-prefix('http://krautchan.net/e'),url-prefix('http://krautchan.net/f'),url-prefix('http://krautchan.net/fb'),url-prefix('http://krautchan.net/k'),
url-prefix('http://krautchan.net/l'),url-prefix('http://krautchan.net/b'),url-prefix('http://krautchan.net/li'),url-prefix('http://krautchan.net/m'),url-prefix('http://krautchan.net/p'),
url-prefix('http://krautchan.net/ph'),url-prefix('http://krautchan.net/t'),url-prefix('http://krautchan.net/tv'),url-prefix('http://krautchan.net/w'),url-prefix('http://krautchan.net/we'),
url-prefix('http://krautchan.net/rfk'),url-prefix('http://krautchan.net/kc'),url-prefix('http://krautchan.net/z'),url-prefix('http://krautchan.net/x'),url-prefix('http://krautchan.net/sp'),
url-prefix('http://krautchan.net/fit'),url-prefix('http://krautchan.net/we'),url-prefix('http://krautchan.net/jp'),url-prefix('http://krautchan.net/h'),url-prefix('http://krautchan.net/s'){

/*Antwortfeld*/

body {}


body > center > img
{ 
display: block!important;
position:static	!important;
margin-top:14px!important;

} /* Banner soll da bleiben*/


div[style="clear: both"]{background:none!important;}

/* KC-Farben:
Dunkelblau: #313370
Hellblau: #AAAACC
Hintergrund: #EEEEEE
*/

ul{display: none !important;}
body{margin: 0 !important;margin-top:30px!important;}

#postform_table{background-color: #EEEEEE !important;}

h1{	margin-top: 20px !important;}

h1:before{content: "";}

/*Navigationsleiste*/
/*nix mit Tooltips*/
body > div[style="float: left"] 
{
	float: left !important;
	left: 0px !important;
	padding: 8px !important;
	position: fixed !important;
	right: 0px !important;
	top: 0px !important;
	width: 100% !important;
	color: gray !important;
	text-align: center !important;
	
	
background: #5b5b5b; /* Old browsers */
background: -moz-linear-gradient(top,  #5b5b5b 0%, #000000 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5b5b5b), color-stop(100%,#000000)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #5b5b5b 0%,#000000 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #5b5b5b 0%,#000000 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #5b5b5b 0%,#000000 100%); /* IE10+ */
background: linear-gradient(to bottom,  #5b5b5b 0%,#000000 100%); /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5b5b5b', endColorstr='#000000',GradientType=0 ); /* IE6-9 */

-moz-box-shadow: 3px 3px 4px #000;
-webkit-box-shadow: 3px 3px 4px #000;
box-shadow: 3px 3px 4px #000;
	
}


body > div[style="float: left"] > a {
color: gray !important;
text-decoration: none !important;
-webkit-transition: all 1s ease-in-out; 
-moz-transition: all 1s ease-in-out; 
-o-transition: all 1s ease-in-out; 
-ms-transition: all 1s ease-in-out; 
transition: all 1s ease-in-out; 
}

body > div[style="float: left"] > a:hover {
color: white !important;
text-decoration: underline !important;
font-size: 2em !important; 
}

body > div[style="float: right"] {	display:none;}
/* RSS Container */
/*Seitenzahlennavigation*/

#postform_row_sage{display: table-row!important;}



div[style="position: absolute; left: 5px;"]> table 
{
	border-spacing:0pt !important;
	border: 0pt !important;
	margin-left: auto !important;
	text-align: right !important;
}

div[style="position: absolute; left: 5px;"]> table > tbody > tr >td{border: 0pt !important;}

}
