	table #sign:not(:hover),table .sign:not(:hover),.signatures:not(:hover),.signature:not(:hover){height:0!important;font-size:0!important;}table #sign:not(:hover):before,table .sign:not(:hover):before,.signatures:not(:hover):before,.signature:not(:hover):before{display:block;content:"";width:auto;height:100px!important;border-top:1px dashed #CDCDCD;}

