css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://dollarstorecrafts.com/") {

body, #front-list, #bottom, #content, #sidebar, #page, #sidebar-top, #sidebar-bottom {
 background: #000!important;
}

#topad, #sidebar-ads, #google_ads_frame3, #sidebar-bottom, #sidebar-middle {
 display: none!important;
}

a:link, a:visited {
 text-decoration: none!important;
 padding: 3px!important;
}

a:link:hover, a:visited:hover {
 text-decoration: none!important;
 color: #ff0!important;
}

p {
 text-decoration: none!important;
 color: #fff!important;
}

.category p {
 text-decoration: none!important;
 color: #000!important;
}

#front-list .clearfloat:hover, #archive .clearfloat:hover {
 background: -moz-linear-gradient(top, rgba(45,1,0,1) 0%, rgba(114,21,21,1) 50%, rgba(119,19,19,1) 51%, rgba(0,0,0,1) 100%)!important;
 color: #fff!important;
}

#navbar, #page-bar a, #page-bar li li a, #page-bar li li a:link, #page-bar li li a:visited {
 background: -moz-linear-gradient(top, rgba(45,1,0,1) 0%, rgba(114,21,21,1) 50%, rgba(119,19,19,1) 51%, rgba(0,0,0,1) 100%)!important;
 color: #fff!important;
}

#page-bar a:hover, #page-bar li li a:hover, #page-bar li li a:link:hover, #page-bar li li a:visited:hover {
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(119,19,19,1) 49%, rgba(114,21,21,1) 50%, rgba(45,1,0,1) 100%)!important;
 color: #ff0!important;
}

.post img, .post a img, #front-list img, #archive img {
 opacity: .7!important;
 margin: 25px 0px -5px 0px!important;
}

.post img:hover, .post a img:hover, #front-list img:hover, #archive img:hover {
 opacity: 1!important;
}











}
