css:
/* Make Accessible in all domains/urls that Gmail should exist for*/

@-moz-document domain('mail.google.com') {
	


/* make height of top ribbon higher to accommodate larger buttons */
#gtdi-rb .gtdi-rb-row-main-item, #gtdi-rb .gtdi-rb-todo-dropdown {
height:2em !important;
}

/* general styles for all large counts */
.gtdi-rb-row-main-item .gtdi-count, .gtdi-rb-row-main-item .gtdi-circlecount {
border-radius: 3px;
border: 2px solid black;
display: inline-block;
font-size: 25px;
font-weight: bold;
padding: .05em .2em;
margin: -.1em .001em .001em .001em;
line-height: 24px;
}

/* Action */
div[gtdi-rbtododropdown-item-id="action"] .gtdi-count  {
border-color: black;
background: #E077E0;
color: #A7219C
}
 
/* Next Action */
div[gtdi-rbtododropdown-item-id="next_action"] .gtdi-count  {
border-color: yellow;
background: red;
color: white;
}

/* Waiting On */
div[gtdi-rbtododropdown-item-id="waiting_on"] .gtdi-count  {
border-color: #00A0D3;
background: #93B9EB;
color: #DDEAEC;
} 

/* Some Day Maybe */
div[gtdi-rbtododropdown-item-id="some_day"] .gtdi-count  {
border-color: #CCC;
background: #D6E5F8;
color: #A8D3DB;
}

/* Today */
div[gtdi-rbtododropdown-item-id="deadline/Today"] .gtdi-count  {
border-color: black;
background: #F5FF00;
color: black;
}

/* Tomorrow */
div[gtdi-rbtododropdown-item-id="deadline/Tomorrow"] .gtdi-count  {
border-color: black;
background: orange;
color: black;
}

/* Upcoming (Next Quarter) */
div[gtdi-rbtododropdown-item-id="deadline/NextQuarter"] .gtdi-count  {
border-color: #51CE0A;
background: #03EC6A;
color: #98B115;
}

/* Overdue */
div[gtdi-rbtododropdown-item-id="deadline/OverdueRecent"] .gtdi-count  {
border-color: #FFB800;
background: #E60000;
color: yellow;
}

/* Overdue (Older Than 2 Months) */
div[gtdi-rbtododropdown-item-id="deadline/OverdueOlder"] .gtdi-count  {
border-color: red;
background: brown;
color: #EFEFEF;
}

/* Overdue (Older Than 4 Months) */
div[gtdi-rbtododropdown-item-id="deadline/OverdueOlderStill"] .gtdi-count  {
border-color: red;
background: black;
color: #EFEFEF;
}

/* All To Do's */
div[gtdi-rbtododropdown-item-id="activeall"] .gtdi-count  {
border-color: black;
background: white;
color: black;
}



/* Zero counts*/
span.gtdi-count-zero {
border-color: #DDD !important;
background: #E7E7E7 !important;
color: #C5C5C5 !important;
font-size: 1em !important;
font-weight: normal !important;
line-height: 1em !important;
}


/* The next set of styles is for creating a highlight around notes */
.gtdi-notesviewer-noteeditor {
background-color: yellow !important;
}

.gtdi-notesviewer {
background-color: yellow;
font-weight:bold;
}

.gtdi-hab-row-notes-content {
padding-bottom: 7px;
background-color: yellow;
border: solid black 1px;
border-left: 0;

}

.gtdi-hab-row-notes-title {
background-color: yellow;
border: solid black 1px;
border-right: 0;
padding-left:5px;
}

.gtdi-notes {

    border: solid black 1px;
    background-color: yellow;
    color: #666;
    font-weight: bold;
}
.gtdi-hab-row-notes-content {

background-color: yellow;
border: solid black 1px;
border-left: 0;

}

.gtdi-hab-row-notes-title {
background-color: yellow;
border: solid black 1px;
border-right: 0;
padding-left:5px;
padding-bottom:8px;
font-size:.9em;
}

.gtdi-notes {

    border: solid black 1px;
    background-color: yellow;
    color: #666;
    font-weight: bold;
}
}
