body#THEPARTY{ background-color: black!important;  background-image: none!important;}
div#header img{ float:left!important; height:30px!important;margin:auto;}
div#header img + img{ margin-left:-15px!important;}
button#party  {-webkit-transform: scale(.5,.5); left:-20px; bottom:auto!important; top:-10px;}
button#party+a{-webkit-transform: scale(.5,.5); right:-20px!important; bottom:auto!important; top: -25px;}


 div#header{width:100%;margin:auto;margin-top:-5px;position:absolute;z-index:100;display:none;}
 body#THEPARTY{margin:0 auto!important;}

 table, tbody, tr, td{display: block; position: relative; width:auto; height:auto; overflow:hidden; margin:0 auto!important; width:100%!important;}
 table{width:100%;}
 tr{width:90%!important;}
 td{display:block!important; float:left!important; margin:0 auto!important;}
 td[width='40%']{width: 100%!important; height:100%!important;overflow:visible;}
 object{display:block;width:100%!important;height:100%!important; margin:0 auto!important;}

 div.selector{overflow:hidden;margin:0 auto!important;}
 div.selector input, div.selector button, div.selector .crate{ float:left!important;}
 div.selector button{padding:5px;}
 div.pages{padding:10px;}
 .crate-title{display:none;}

 a.crate-vid{float:left;}
 div.pages{float:left;}


 object#right-player{position:relative!important;top:37px!important;}
 td[width='40%']:last-child{margin-bottom:37px!important;}
 div#right-selector{ position:absolute;top:0!important;}

 td[width='20%']{ width: 50%!important; margin:10px 25%!important;}
 td[width='20%'] img[src='down-arrow.gif']{ display:none!important;}
 div#crossfader{margin-top:0!important;}

@media screen and (max-width: 675px) {
 object#right-player{important;top:105px!important;}
 td[width='40%']:last-child{margin-bottom:105px!important;}
}
@media screen and (max-width: 1148px) and (min-width: 676px){
 object#right-player{important;top:71px!important;}
 td[width='40%']:last-child{margin-bottom:71px!important;}
}
