css:
@media screen and (max-width: 640px)
{
	#container {
		width:90%;
		min-width:300px;
	}

	.menu_langue li {
		background-color: white;
		float: none;
		list-style: none;
		margin: 4px 2px;
		padding: 3px;
		border: 1px solid #357284;
		width: 1.4em;
		text-align: center;
	}
	.menu_langue {
		font-weight:normal;
	}

	#sitename {
		color: white;
		height: 160px;
		overflow: hidden;
		border: 0;
		margin: 0;
		padding: 0;
		text-align: left;
		font-weight: normal;
		width: auto;
		min-width:270px;
	}

	#sitename a, #sitename img {
		margin: 0;
		padding: 0;
		height: 160px;
	}
	#mainmenu a:hover, #mainmenu a.current{
		border : none;
	}
	#mainmenu a, #mainmenu div.pseudolink{
		border : none;
		padding:5px 6px;
		margin:0;
	}

	#mainmenu a, #mainmenu div.pseudolink {
		margin-right: 1px;
		font-size: 0.76em;
	}
	.fil_ariane {
		margin :0;
		border-top:2px solid white;
		border-bottom : 2px solid white;
		border-left : none;
		border-right : none;
	}
	#leftside{
		min-height:auto;
		height:auto;
		width:auto;
		min-width:290px;
		float:none;
	}
	.nav{
		width:auto;
		font-size:1.2em;
	}

	#content {
		min-width: 290px;
		width: 100%;
		padding: 0;
		background-color:white;

	}
	#content p {
		margin : 0;
		padding : 0 5px;
	}
	#content h2{
		padding-left:10px;
		margin:8px 0 0;
		font-size:1.3em;
	}
	#content h3 {
		font-size: 1.4em;
	}
	#content p {
		margin: 0;
		font-size: 1.3em;
		font-weight: bold;
	}

	#footer{
		width: 90%!important;
		min-width:300px!important;
	}
	#rightside2 {
		display: none;
	}

	#rightside1{
		padding-right:4px;
		margin:0;
		float:none;
		min-width:292px;
		width:auto;
		background:white;
	}
	#rightside1 hr{
		display:none;
	}
	#rightside1 h2{
		margin-top:5px;
		border-top:1px solid lightgray;
		padding-top:5px;
		font-size:1.2em;
	}

}
