/*Responsive jusque 989px*/

@media (max-width:989px)
{    
    body.rouge,
    body.vert,
    body.orange,
    body.bleu {
        background: none !important;
    }
    
    * img
    {
        max-width:100% !important;
        height:auto !important;
    }
    
    #page
    {
        width: auto !important;
    }
    
    /*header */
    #head img
    {
        width:100% !important;
        height:auto !important;
    }
    
    #page #head #links_head
    {
        top: 70px !important;
    }
    
    /*menu*/
    
    #page #menu ul li
    {
        height:30px !important;
        line-height:30px !important;
        padding: 0 3% 0 0 !important;
        width:30% !important;
    }
    
    
    /*content */
    
    #page #wrapper #breadcrumbs
    {
        padding: 0 !important;
    }
    #page #wrapper #content {
        padding: 0 5px 0 5px !important;
    }
    #page #c159 {
        margin: 0 !important;
        width: auto !important;
    }
    #page #c159 p {
        width: auto !important;
    }
    .news-list-container.news_home .news-list-item {
        float:left !important;
        margin: 0 !important;
        width: 50% !important;
        text-align:center !important;
    }
    .news-list-container.news_actu .news-list-item {
        height: 250px !important;
        width:50% !important;
        margin: 0 0 0 0 !important;
    }
    .news-list-container.news_actu .news-list-item .news_image {
    
        margin: 0 10% 10px 0 !important;
        width: 40% !important;
    }
    .news-list-container.news_actu .news-list-item .news_text {
    
        width: 50% !important;
    }
    
    .csc-textpic div
    {
        margin-left: 0 !important;
    }
    
    
    #page #wrapper .twoCol #left {
        float: none !important;
        width: auto !important;
    }
    
    .news-single-item .imageContentNews {
        float: none !important;
        margin: 10px 0 0 0 !important;
        width: auto !important;
    }
    .news-single-img {
        float: none !important;    
    }
    
    .news-single-imgcaption
    {
        width:auto !important;
    }
    
    .news-single-item .textContentNews {
        float: none !important;    
        width: auto !important;
    }
    #page #wrapper .twoCol #right
    {
        float:none !important;
        width:auto !important;
    }
    
    .csc-textpic-imagewrap,
    .csc-textpic-imagerow,
    .csc-textpic-text{
        width: auto !important;
        word-wrap:break-word !important;
    }
    #left ul li a {
        width: 30% !important;
    }
    /* footer */
    
    
    #page #footer {
        padding: 28px 0 !important;
    }
    #page #footer #c838 {
        margin-right: 0 !important;
        width: 100% !important;
        padding: 0 0 10px 0 !important;
    }
}
 /*Responsive jusque 640px*/
 
@media (max-width:640px)
{   

    #page #c159 
    {
        margin: 0 !important;
        width: auto !important;
    }
    .news-list-container.news_home .news-list-item {
        float:none !important; 
        margin: 0 !important;
        width: 100% !important;
        text-align:center !important;
    }
    .news-list-container.news_actu .news-list-item {
        height: auto !important;
        width:100% !important;
        margin: 0 0 0 0 !important;
    }

    #left ul li a {
        float: none !important;
        display:inline-block !important;  
        height: 50px !important;
        width: 98% !important;
    }
	    #page #footer #c838 {
        margin-right: 0 !important;
        width: auto !important;
        padding: 0 0 10px 0 !important;
    }
    DIV.csc-textpic-intext-left-nowrap DIV.csc-textpic-imagewrap {
        float: none !important;
    }
}
