css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.repinly.com"), url-prefix("https://www.facebook.com/") {

body, .pin_lnk_out, .oth_pins_pins, .other_pins, .hp_board, .stats_box, .stats_all_hold {
 background: #000!important;
}

a#aw0, .ads_top, embed, #cbc, #google_image_div a img, .h_ads {
 display: none!important;
}

a, p, span {
 color: #fff!important;
}

.stats_boxl_tit span, .pop_categ {
 color: #000!important;
}

.itmc_spons a:hover, .hp_fmore1:hover, .pboards_info span a:hover, .hp_itmin a.allvid_a:hover, .stats_lnk a:hover, .hp_itmin strong, .pop_categ_l span a:hover {
 color: #ff0!important;
 text-decoration: none!important;
}

h1, h2, h3, .pn_rpns_t:hover, .h_list li div a:hover, .sub_nav li div a:hover, .stats_boxtit, .addy_to:hover, .srch_sqr a:hover, .pnr_inftit {
 color: #ff0!important;
}

.hp_itmp {
 opacity: .7!important;
}

.hp_itmp:hover {
 opacity: 1!important;
}

.hp_f_board, .pn_repins, .s_list li div { /* Blue */
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,121,196,1) 50%, rgba(0,0,0,1) 100%)!important;
}

.hp_itmi { /* Brown */
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(175,85,0,1) 50%, rgba(0,0,0,1) 100%)!important;
}

.hp_f_board:hover, .pn_repins:hover, .pboards_itm:hover, .hu_itm:hover { /* Darker Blue */
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,95,155,1) 50%, rgba(0,0,0,1) 100%)!important;
}

.head_in, .s_list li div.sort_s, .li_plch_c, .r_login, .pnr_bigslv, .pnr_slvbox { /* Red */
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(191,0,3,1) 50%, rgba(0,0,0,1) 100%)!important;
}

.h_list li div.selected, .sub_nav li div.sub_select, .sort_s, .h_list li div.selected, [style="background-color: rgb(219, 218, 218);"], .r_login:hover { /* Green */
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(41,171,0,1) 50%, rgba(0,0,0,1) 100%)!important;
}

.h_list li div, .sub_nav li div, .s_list li div.no_sort {
 background: none!important;
}

.foll_spons_btn { /* Green */
 background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(55,155,0,1) 50%, rgba(0,0,0,1) 100%)!important;
}

.foll_spons_btn a:after {
 position: absolute!important;
 padding: 5px 15px 15px 9px!important;
 content: "Follow"!important;
}

.foll_spons_btn a:hover {
 color: #ff0!important;
}


}
