@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.centrepompidou.fr/fr"), url("http://www.centrepompidou.fr/"), url("http://www.centrepompidou.fr"), url("http://www.centrepompidou.fr/fr/"), url("http://www.centrepompidou.fr/cpv/agenda.action"), url("http://www.centrepompidou.fr/fr/La-visite"), url("http://www.centrepompidou.fr/fr/La-visite/"), url("http://www.centrepompidou.fr/fr/Le-centre-pompidou") , url("http://www.centrepompidou.fr/fr/Le-centre-pompidou/")  {

body { line-height: 1.6 !important;
font-size: 12px!important;}
#page_titre h1{ color: #000 !important;}
#rech_form{ padding-right: 0 !important;}
#menuppal{ min-width: auto !important;
/*position: fixed !important;*/ 
display: block !important;}
#menuppal ul{ width: 100% !important;}
#reglages { width: auto !important; 
min-width: auto !important;
padding-right: 0 !important;
 }
 #espace_perso{ left: -878px !important;}

#p_home6 .class-frontpage, #p_home6 .attribute-page, #p_home6 table, #p_home6 tbody{ width: 100% !important}
#home_screen table td {float:left !important;}
#node_id_468{ position:relative !important;}
#colophon { background: none !important;}

#menuppal .first a span {
    top: -5px !important;}



@media (max-width:1024px){
    #home_screenbox, #colophon_home, #rech_section, #page, #colophon {
        padding-left: 8% !important;
        width: 90% !important;
        min-width: auto  !important;
        margin: 0 !important;
    }
    
    #page_cp{ padding: 2% 0% !important;}
    
    #agendabox {
    min-width: 1000px !important;
    }
    
}

@media (max-width:750px){
    
    #page_titre h1{ text-align: left !important;}
    #p_home6 #sm1, #p_home6 #sm2, #p_home6 #sm3, #p_home6 #sm4, #p_home6 #sm5, #p_home6 #sm6 {
    width: 49% !important;
    float:left !important;
}

    #colohom_liens p{ line-height: 24px !important }
     #rech_form{ 
            width: 100% !important;}
            
    .rech_norm{ 
            width: 92% !important;
            border: double 2px #666!important;
            background: none !important;
            padding: 3% 4% 1% 4% !important;}
            
    #rech_text{height: 30px !important;
    width: 90% !important;
    font-size: 16px !important;
    margin: -5px 5px 0 0 !important;}
    
    .aderouler {
              padding: 0 12px 10px 0!important;
    }
    
    #menuppal .first a { padding-left: 0 !important;
                        background: none !important;
                        border-left: 1px solid #FFFFFF !important;}
    
    #menuppal .first a span {
     visibility:visible !important;
        }
     #menuppal .last a {
    padding-right: 10px !important;
}   
    #colohom_liens{ width: auto !important;}
    
    
    
}

@media (max-width:370px){
#page_titre h1{ font-size: 76px !important;}
#page_cp{ padding-right: 0 !important;}
#menuppal a { clear: both !important;}
#espace_perso{ top: 100px !important;}
}

}
