css:
@-moz-document url-prefix("https://accounts.google.com"), url-prefix("http://accounts.google.com") {
/*Background*/
.wrapper { background-image: url(/*[[backgoundimage]]*/) !important; background-size: auto !important; background-position: center top !important; background-repeat: no-repeat !important; background-attachment: fixed !important;
background-color: transparent !important;}

/*Border*/
.g-button-red, .g-button-red:active, .g-button-red:hover, .signin-box, .accountchooser-box , .signin-box input[type="email"], .signin-box input[type="text"], .signin-box input[type="password"], .g-button-submit:hover, .g-button:hover,
.g-button, .signuponepage .side-content .benefit img:first-child, .signuponepage .signup-box, input.form-error[type="checkbox"], input.form-error[type="email"], input.form-error[type="number"], input.form-error[type="password"],
input.form-error[type="text"], input.form-error[type="url"], .goog-flat-menu-button, .goog-menu, .goog-menuitem, .goog-tristatemenuitem, .goog-filterobsmenuitem, #recaptcha_widget.recaptcha-widget {
	border: 1px solid rgb(/*[[bordercolor]]*/) !important;}

/*Border-Top*/
.google-footer-bar {
	border-top: 1px solid rgb(/*[[bordercolor]]*/) !important;}

/*Border-Right*/
input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"], input[type="email"]:hover, input[type="number"]:hover, input[type="password"]:hover, input[type="tel"]:hover,
input[type="text"]:hover, input[type="url"]:hover, input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"] {
	border-right: 1px solid rgb(/*[[bordercolor]]*/) !important;}

/*Border-Bottom*/
.google-header-bar, .recaptcha-widget #recaptcha_image {
	border-bottom: 1px solid rgb(/*[[bordercolor]]*/) !important;}

/*Border-Color*/
input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"], input[type="email"]:hover, input[type="number"]:hover, input[type="password"]:hover, input[type="tel"]:hover,
input[type="text"]:hover, input[type="url"]:hover, input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"] {
	border-color: rgb(/*[[bordercolor]]*/) !important;}

/*Background-Color*/
.g-button-red, .g-button-red:active, .g-button-red:hover, .product-info, .signin-box, .accountchooser-box, .signin-box input[type="email"], .signin-box input[type="text"], .signin-box input[type="password"], .g-button-submit:hover,
.g-button:hover, .g-button, .footer, .main h1, .signuponepage .side-content .benefit div h2, .signuponepage .side-content .benefit div, .goog-flat-menu-button, .goog-menuitem, .goog-tristatemenuitem, .goog-filterobsmenuitem,
.talk_number_input, .talk-select-country, .signuponepage input[type="tel"], .signuponepage input[type="text"], .signuponepage input[type="password"], .signuponepage select, .signuponepage .why-information,
.recaptcha-widget #recaptcha_reload_btn, .recaptcha-widget #recaptcha_switch_audio_btn, .recaptcha-widget #recaptcha_whatsthis_btn  {
	background-color: rgba(/*[[backgroundcolor]]*//*[[transparents]]*/) !important;}

/*Background-Transparent*/
#recaptcha_widget.recaptcha-widget {
	background: transparent !important;}

/*Background*/
.google-header-bar, .signuponepage .signup-box, input.form-error[type="checkbox"], input.form-error[type="email"], input.form-error[type="number"], input.form-error[type="password"], input.form-error[type="text"],
input.form-error[type="url"], input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"], .goog-menu, input[type="email"], input[type="number"], input[type="password"],
input[type="tel"], input[type="text"], input[type="url"] {
	background: rgba(/*[[backgroundcolor]]*//*[[transparents]]*/) !important;}

/*Color*/
.g-button-red, .g-button-red:active, .g-button-red:hover, .redtext, .product-headers h2, p, .product-info, .product-info h3, .signin-box .reauth, strong, .signin-box input[type="email"], .signin-box input[type="text"],
.signin-box input[type="password"], .g-button-submit:hover, .g-button:hover, .g-button, a, .footer ul li, .signuponepage .side-content .benefit div p:last-child, .signuponepage .form-element .placeholder-text, .goog-flat-menu-button,
.goog-menuitem, .goog-tristatemenuitem, .goog-filterobsmenuitem,.talk_number_input, .signuponepage input[type="tel"], .signuponepage input[type="text"], .signuponepage input[type="password"], .signuponepage select,
.signuponepage .why-information, .signuponepage .form-element .atgmail, .goog-menuitem-highlight .goog-menuitem-content, .goog-menuitem-hover .goog-menuitem-content, .signuponepage .username-suggestions, .header .signin-button,
.header .signup-button   {
	color: rgb(/*[[color]]*/) !important;}

/*Color2*/
.redtext, .signin-box h2, a:-webkit-any-link, .signuponepage .side-content .benefit div h2 {
	color: rgb(/*[[color2]]*/) !important;}

/*Background-image*/
.g-button-red, .g-button-red:active, .g-button-red:hover, .g-button-submit:hover, .g-button:hover, .g-button, .goog-flat-menu-button  {
	background-image: none !important;}

/*Text-Shadow*/
.g-button-red, .g-button-red:active, .g-button-red:hover, .g-button-submit:hover, .g-button:hover, .g-button {
	text-shadow: none !important;}

/*Box-Shadow*/
.g-button-red, .g-button-red:active, .g-button-red:hover, .g-button-submit:hover, .g-button:hover, .g-button, .goog-menu, input[type="email"]:hover, input[type="number"]:hover, input[type="password"]:hover, input[type="tel"]:hover,
input[type="text"]:hover, input[type="url"]:hover {
	box-shadow: none !important;}}
