}

span.post_like_link, .like_link, #pv_like_wrap span.fl_l:first-child, #mv_like_wrap span.fl_l:first-child {
    display:none!important;
}

#owner_photo_input .upload_btn {
    font-size: 10px!important;
    padding: 5px 16px!important;
}




