}
#profile_avatar img, #group_avatar img, .content a img {
    border-radius: 4px !important;

}
div.fave_face_name {
    border-bottom-left-radius: 4px !important;
    -o-border-bottom-left-radius: 4px !important;
    -webkit-border-bottom-left-radius: 4px !important;
    border-bottom-right-radius: 4px !important;
    -o-border-bottom-right-radius: 4px !important;
    -webkit-border-bottom-right-radius: 4px !important;
}

/* Кнопка "Назад" в шапке сайта */

#side_back_link {
margin-left: 560px !important;
margin-top: 4px !important;
color: #e8eff7 !important;
padding: 3px 6px !important;
padding-left: 19px !important;
background-position: 0px -5px!important;
background-color: transparent !important;
background-image: url("data:image/gif;base64,") !important;
font-weight:bold!important;
}
/* Уменьшение контакт-листа с онлайн пользователями по высоте */
#fc_contacts {
	height: 567px !important;
    z-index: 300!important;

}
.fc_clist_inner {
	height: 60% !important;
    right: 0!important;
    top: 0!important;
    z-index: 300!important;

}
/* Закругление блоков на профиле*/ 
.wr_header, #owner_photo_bubble, .module_header .header_top, #photos_albums .photo_album_title {
