}
#public .help_steps_module {
	display: none !important;
}
.stl_text {
	display: none !important;
}
.group_wiki_wrap .wk_text {display: block !important}
}
#pad_wrap {z-index: 702 !important}
#wrap3, #ads_page_wrap3, #header_wrap3 {background-color:#fff!important; border-radius: 0 0 5px 5px !important}

#content .my_current_info, #content .my_current_info *, #content .current_text {color: #AAAAAA !important}

.top_info_bar .info {
    padding: 8px!important;
